// src/components/ChartCreator.jsx
import React, { useState, useMemo } from "react";
import HighchartsReact from "highcharts-react-official";
import Highcharts from "./utils/highcharts-config"; // your custom Highcharts build with drilldown module enabled

const chartTypes = [
  "line",
  "bar",
  "column",
  "pie",
  "scatter",
  "area",
  "bubble",
  "spline",
  "areaspline",
];

export default function ChartCreator({ data, onSave }) {
  const [chartType, setChartType] = useState("column");
  const [xKey, setXKey] = useState("");
  const [yKey, setYKey] = useState("");
  const [drilldownKeys, setDrilldownKeys] = useState([]); // multiple drilldown keys now
  const [title, setTitle] = useState("My Chart");

  const keys = useMemo(() => (data.length > 0 ? Object.keys(data[0]) : []), [data]);

  const handleDrilldownKeysChange = (e) => {
    // Multi-select returns selected options as an array
    const selectedOptions = Array.from(e.target.selectedOptions, (option) => option.value);
    setDrilldownKeys(selectedOptions);
  };

  // Build series and drilldown series for Highcharts drilldown feature
  const { series, drilldown } = useMemo(() => {
    if (!xKey || !yKey) return { series: [], drilldown: undefined };

    const grouped = {};
    const drilldownSeries = [];

    data.forEach((row) => {
      const main = row[xKey];
      const yVal = Number(row[yKey]) || 0;

      if (!grouped[main]) grouped[main] = { total: 0, drill: {} };
      grouped[main].total += yVal;

      if (drilldownKeys.length > 0) {
        drilldownKeys.forEach((key) => {
          const subKey = row[key] ?? "(empty)";
          if (!grouped[main].drill[key]) grouped[main].drill[key] = {};
          grouped[main].drill[key][subKey] = (grouped[main].drill[key][subKey] || 0) + yVal;
        });
      }
    });

    // Build main series points
    const mainSeriesMultiple = [];

    Object.entries(grouped).forEach(([mainName, { total, drill }]) => {
      if (drilldownKeys.length === 0) {
        mainSeriesMultiple.push({ name: mainName, y: total, drilldown: null });
      } else {
        drilldownKeys.forEach((dKey) => {
          const subDrill = drill[dKey] || {};
          const sum = Object.values(subDrill).reduce((a, b) => a + b, 0);
          const drilldownId = `${mainName}_${dKey}`;

          mainSeriesMultiple.push({
            name: `${mainName} (${dKey})`,
            y: sum,
            drilldown: drilldownId,
          });

          drilldownSeries.push({
            id: drilldownId,
            name: `${mainName} - ${dKey} details`,
            data: Object.entries(subDrill).map(([subName, value]) => [subName, value]),
          });
        });
      }
    });

    return {
      series: [
        {
          name: yKey,
          colorByPoint: true,
          data: mainSeriesMultiple,
        },
      ],
      drilldown:
        drilldownSeries.length > 0
          ? {
              series: drilldownSeries,
              activeDataLabelStyle: {
                color: "#FFFFFF",
                textDecoration: "none",
                fontWeight: "normal",
              },
            }
          : undefined,
    };
  }, [data, xKey, yKey, drilldownKeys]);

  // Chart options
  const options = {
    chart: { type: chartType },
    title: { text: title },
    xAxis: {
      type: "category",
      title: { text: xKey || null },
    },
    yAxis: {
      title: { text: yKey || null },
    },
    legend: { enabled: true },
    plotOptions: {
      series: {
        borderWidth: 0,
        dataLabels: { enabled: true },
      },
    },
    series,
    drilldown,
  };

  // Save handler: export options as JSON
  const handleSave = () => {
    const configJson = JSON.stringify(options, null, 2);
    onSave(title, configJson);
  };

  return (
    <div style={{ padding: "1rem", maxWidth: 700 }}>
      <h3>Create Chart with Drilldown</h3>

      <input
        placeholder="Chart Title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        style={{ marginBottom: 12, width: "100%", padding: 6, fontSize: 16 }}
      />

      <div style={{ display: "flex", gap: 12, flexWrap: "wrap", marginBottom: 12 }}>
        <select
          value={chartType}
          onChange={(e) => setChartType(e.target.value)}
          style={{ flex: 1, minWidth: 120, padding: 6 }}
        >
          {chartTypes.map((type) => (
            <option key={type} value={type}>
              {type}
            </option>
          ))}
        </select>

        <select
          value={xKey}
          onChange={(e) => setXKey(e.target.value)}
          style={{ flex: 1, minWidth: 120, padding: 6 }}
        >
          <option value="">Select X-axis</option>
          {keys.map((k) => (
            <option key={k} value={k}>
              {k}
            </option>
          ))}
        </select>

        <select
          value={yKey}
          onChange={(e) => setYKey(e.target.value)}
          style={{ flex: 1, minWidth: 120, padding: 6 }}
        >
          <option value="">Select Y-axis</option>
          {keys.map((k) => (
            <option key={k} value={k}>
              {k}
            </option>
          ))}
        </select>

        <select
          multiple
          value={drilldownKeys}
          onChange={handleDrilldownKeysChange}
          style={{ flex: 1, minWidth: 180, padding: 6, height: 100 }}
          title="Select one or more drilldown keys (Ctrl+click or Cmd+click to select multiple)"
        >
          {keys.map((k) => (
            <option key={k} value={k}>
              {k}
            </option>
          ))}
        </select>
      </div>

      <HighchartsReact highcharts={Highcharts} options={options} />

      <button
        onClick={handleSave}
        style={{
          marginTop: 12,
          padding: "8px 16px",
          fontSize: 16,
          cursor: "pointer",
        }}
        disabled={!xKey || !yKey}
        title={!xKey || !yKey ? "Select X and Y axis keys first" : "Save Chart Config"}
      >
        Save Chart
      </button>
    </div>
  );
}
