import React, { useState } from "react";
import HighchartsReact from "highcharts-react-official";
import Highcharts from "highcharts";
import { Link } from "react-router-dom";

const mainData = [
  { Year: "2010", TotalBudget: 11804.2, TotalResource: 10316.1, TotalCapital: 1488.1 },
  { Year: "2011", TotalBudget: 11520.4, TotalResource: 10329.1, TotalCapital: 1191.3 },
  { Year: "2012", TotalBudget: 11525.9, TotalResource: 10353.4, TotalCapital: 1172.5 },
  { Year: "2013", TotalBudget: 11548.7, TotalResource: 10431.9, TotalCapital: 1116.8 },
  { Year: "2014", TotalBudget: 11919.6, TotalResource: 10519.9, TotalCapital: 1399.7 },
  { Year: "2015", TotalBudget: 11293.8, TotalResource: 10176.1, TotalCapital: 1117.7 },
  { Year: "2016", TotalBudget: 11614, TotalResource: 10398, TotalCapital: 1216 },
  { Year: "2017", TotalBudget: 11845.1, TotalResource: 10612.8, TotalCapital: 1232.3 },
  { Year: "2018", TotalBudget: 12204.7, TotalResource: 10780.4, TotalCapital: 1424.3 },
  { Year: "2019", TotalBudget: 12741.6, TotalResource: 11351.5, TotalCapital: 1390.1 },
  { Year: "2020", TotalBudget: 13746.4, TotalResource: 12196.9, TotalCapital: 1549.5 },
  { Year: "2021", TotalBudget: 14782.5, TotalResource: 13001.5, TotalCapital: 1781 },
  { Year: "2022", TotalBudget: 16323.341, TotalResource: 14269.097, TotalCapital: 2054.244 },
  { Year: "2023", TotalBudget: 16451.684, TotalResource: 14211.967, TotalCapital: 2239.717 },
  { Year: "2024", TotalBudget: 17255.6, TotalResource: 15168.2, TotalCapital: 2087.4 },
  { Year: "2025", TotalBudget: 19085, TotalResource: 16639.4, TotalCapital: 2445.6 },
];

const drilldownData = {
  "2010": {
    categories: ["Dept A", "Dept B", "Dept C"],
    resource: [3000, 4000, 3316.1],
    capital: [488.1, 500, 500],
  },
  "2011": {
    categories: ["Dept A", "Dept B", "Dept C"],
    resource: [3029.1, 4029.1, 3270.9],
    capital: [391.3, 400, 400],
  },
  // Add more years similarly
};

const DashboardPage = () => {
  const [chartOptions, setChartOptions] = useState({
    chart: { type: "column", backgroundColor: "#1e2a38" },
    title: {
      text: "Northern Ireland Departmental Budget and Spend",
      style: { fontWeight: "bold", fontSize: "20px", color: "white", textOutline: "2px black" },
    },
    subtitle: {
      text: "Click on a year to view detailed departmental budgets",
      style: { color: "white" },
    },
    legend: {
      enabled: true,
      itemStyle: { color: "white" },
      itemHoverStyle: { color: "#ccc" },
    },
    xAxis: {
      categories: mainData.map((item) => item.Year),
      title: { text: "Year", style: { color: "white" } },
      labels: { style: { color: "white" } },
      crosshair: true,
    },
    yAxis: {
      min: 0,
      title: { text: "Budget (£ millions)", style: { color: "white" } },
      labels: { style: { color: "white" } },
      gridLineColor: "rgba(255,255,255,0.2)",
    },
    tooltip: {
      shared: true,
      backgroundColor: "#2c3e50",
      style: { color: "white" },
      formatter: function () {
        let s = `<b>Year: ${this.x}</b>`;
        this.points.forEach((point) => {
          s += `<br/><span style="color:${point.color}">●</span> ${point.series.name}: <b>£${point.y.toLocaleString()}</b>`;
        });
        return s;
      },
    },
    plotOptions: {
      column: { stacking: false, dataLabels: { enabled: false } },
      series: {
        cursor: "pointer",
        point: {
          events: {
            click: function () {
              const year = this.category;
              if (!drilldownData[year]) return;
              const { categories, resource, capital } = drilldownData[year];
              setChartOptions((prev) => ({
                ...prev,
                title: {
                  text: `Detailed Departmental Budgets for ${year}`,
                  style: prev.title.style,
                },
                subtitle: {
                  text: `<a href="#" style="color:#fff; text-decoration: underline;">Back to overview</a>`,
                  useHTML: true,
                  style: { color: "white" },
                },
                xAxis: {
                  categories,
                  title: { text: "Department", style: { color: "white" } },
                  labels: { style: { color: "white" } },
                },
                series: [
                  { name: "Resource Budget", data: resource, color: "#7cb5ec" },
                  { name: "Capital Budget", data: capital, color: "#434348" },
                ],
              }));
            },
          },
        },
      },
    },
    series: [
      {
        name: "Total Resource",
        data: mainData.map((item) => item.TotalResource),
        color: "#7cb5ec",
      },
      {
        name: "Total Capital",
        data: mainData.map((item) => item.TotalCapital),
        color: "#434348",
      },
    ],
    credits: { enabled: false },
  });

  const handleSubtitleClick = (e) => {
    if (e.target.tagName === "A" && e.target.textContent.includes("Back")) {
      e.preventDefault();
      setChartOptions((prev) => ({
        ...prev,
        title: {
          text: "Northern Ireland Departmental Budget and Spend",
          style: prev.title.style,
        },
        subtitle: {
          text: "Click on a year to view detailed departmental budgets",
          style: { color: "white" },
        },
        xAxis: {
          categories: mainData.map((item) => item.Year),
          title: { text: "Year", style: { color: "white" } },
          labels: { style: { color: "white" } },
        },
        series: [
          {
            name: "Total Resource",
            data: mainData.map((item) => item.TotalResource),
            color: "#7cb5ec",
          },
          {
            name: "Total Capital",
            data: mainData.map((item) => item.TotalCapital),
            color: "#434348",
          },
        ],
      }));
    }
  };

  return (
    <div onClick={handleSubtitleClick} style={{ padding: "1rem", backgroundColor: "#1e2a38" }}>
      <HighchartsReact highcharts={Highcharts} options={chartOptions} />
      <div style={{ marginTop: "1rem" }}>
        <Link to="/" style={{ color: "#ddd" }}>← Back to Home</Link>
      </div>
    </div>
  );
};

export default DashboardPage;
