// src/components/ChartCreator.jsx
import React, { useState, useMemo } from "react";
import HighchartsReact from "highcharts-react-official";
import Highcharts from "./utils/highcharts-config";

const chartTypes = [
  "line",
  "bar",
  "column",
  "pie",
  "scatter",
  "area",
  "bubble",
  "spline",
  "areaspline",
];

export default function ChartCreator({ data, onSave }) {
  const [chartType, setChartType] = useState("column");
  const [xKey, setXKey] = useState("");
  const [yKey, setYKey] = useState("");
  const [drilldownKeys, setDrilldownKeys] = useState([]);
  const [title, setTitle] = useState("My Chart");

  const keys = useMemo(() => (data.length > 0 ? Object.keys(data[0]) : []), [data]);

  const handleDrilldownKeysChange = (e) => {
    const selectedOptions = Array.from(e.target.selectedOptions).map((o) => o.value);
    setDrilldownKeys(selectedOptions);
  };

  const { series, drilldown } = useMemo(() => {
    if (!xKey || !yKey) return { series: [], drilldown: undefined };

    // Group data by main xKey
    const grouped = {};
    const drilldownSeries = [];

    data.forEach((row) => {
      const main = row[xKey];
      const yVal = Number(row[yKey]) || 0;

      if (!grouped[main]) grouped[main] = { total: 0, drill: {} };
      grouped[main].total += yVal;

      drilldownKeys.forEach((key) => {
        const subKey = row[key] ?? "(empty)";
        if (!grouped[main].drill[key]) grouped[main].drill[key] = {};
        grouped[main].drill[key][subKey] = (grouped[main].drill[key][subKey] || 0) + yVal;
      });
    });

    // Main series: one point per main category, drilldown id = main category
    const mainSeriesPoints = Object.entries(grouped).map(([mainName, { total }]) => ({
      name: mainName,
      y: total,
      drilldown: drilldownKeys.length ? mainName : null,
    }));

    // Drilldown series: one series per main category with multiple columns (one per drilldown key)
    drilldownKeys.length &&
      Object.entries(grouped).forEach(([mainName, { drill }]) => {
        // Each drilldown series contains multiple columns,
        // each representing one drilldown key's breakdown under that main category
        const dataForDrilldown = drilldownKeys.map((dKey) => {
          const breakdown = drill[dKey] || {};
          const totalForKey = Object.values(breakdown).reduce((a, b) => a + b, 0);

          // We show sum for each drilldown key as a column in the drilldown chart
          return {
            name: dKey,
            y: totalForKey,
            drilldown: `${mainName}__${dKey}`, // allow nested drilldown (optional)
          };
        });

        drilldownSeries.push({
          id: mainName,
          name: `${mainName} drilldown`,
          data: dataForDrilldown.map((item) => [item.name, item.y]),
        });

        // Optionally, you can implement nested drilldowns here by adding further series for each `${mainName}__${dKey}`
        // But for now, we keep it single-level drilldown for simplicity
      });

    return {
      series: [
        {
          name: yKey,
          colorByPoint: true,
          data: mainSeriesPoints,
        },
      ],
      drilldown:
        drilldownSeries.length > 0
          ? {
              series: drilldownSeries,
              activeDataLabelStyle: {
                color: "#FFFFFF",
                textDecoration: "none",
                fontWeight: "normal",
              },
            }
          : undefined,
    };
  }, [data, xKey, yKey, drilldownKeys]);

  const options = {
    chart: { type: chartType },
    title: { text: title },
    xAxis: {
      type: "category",
      title: { text: xKey || null },
    },
    yAxis: {
      title: { text: yKey || null },
    },
    legend: { enabled: true },
    plotOptions: {
      series: {
        borderWidth: 0,
        dataLabels: { enabled: true },
      },
    },
    series,
    ...(drilldown && { drilldown }),
  };

  const handleSave = () => {
    const configJson = JSON.stringify(options, null, 2);
    onSave(title, configJson);
  };

  return (
    <div style={{ padding: "1rem", maxWidth: 600 }}>
      <h3>Create Chart with Drilldown</h3>

      <input
        placeholder="Chart Title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        style={{ marginBottom: 12, width: "100%", padding: 6, fontSize: 16 }}
      />

      <div style={{ display: "flex", gap: 12, flexWrap: "wrap", marginBottom: 12 }}>
        <select
          value={chartType}
          onChange={(e) => setChartType(e.target.value)}
          style={{ flex: 1, minWidth: 120, padding: 6 }}
        >
          {chartTypes.map((type) => (
            <option key={type} value={type}>
              {type}
            </option>
          ))}
        </select>

        <select
          value={xKey}
          onChange={(e) => setXKey(e.target.value)}
          style={{ flex: 1, minWidth: 120, padding: 6 }}
        >
          <option value="">Select X-axis</option>
          {keys.map((k) => (
            <option key={k} value={k}>
              {k}
            </option>
          ))}
        </select>

        <select
          value={yKey}
          onChange={(e) => setYKey(e.target.value)}
          style={{ flex: 1, minWidth: 120, padding: 6 }}
        >
          <option value="">Select Y-axis</option>
          {keys.map((k) => (
            <option key={k} value={k}>
              {k}
            </option>
          ))}
        </select>

        <select
          multiple
          value={drilldownKeys}
          onChange={handleDrilldownKeysChange}
          style={{ flex: 1, minWidth: 200, padding: 6, height: 100 }}
          title="Select one or more drilldown keys (Ctrl+click or Cmd+click to select multiple)"
        >
          {keys.map((k) => (
            <option key={k} value={k}>
              {k}
            </option>
          ))}
        </select>
      </div>

      <HighchartsReact highcharts={Highcharts} options={options} />

      <button
        onClick={handleSave}
        style={{
          marginTop: 12,
          padding: "8px 16px",
          fontSize: 16,
          cursor: "pointer",
        }}
        disabled={!xKey || !yKey}
        title={!xKey || !yKey ? "Select X and Y axis keys first" : "Save Chart Config"}
      >
        Save Chart
      </button>
    </div>
  );
}
