// src/components/ChartCreator.jsx
import React, { useState, useMemo } from "react";
import HighchartsReact from "highcharts-react-official";
import Highcharts from "./utils/highcharts-config";

const chartTypes = ["column", "bar", "line", "pie", "area"];

export default function ChartCreator({ data, onSave }) {
  const keys = Object.keys(data[0] || {});
  const [chartType, setChartType] = useState("column");
  const [xKey, setXKey] = useState(() => keys[0] || "");
  const [yKey, setYKey] = useState(() => keys[1] || "");
  const [drilldownKeys, setDrilldownKeys] = useState([]);
  const [title, setTitle] = useState("My Chart");

  // New state for drilldown mode toggle
  const [inlineDrilldown, setInlineDrilldown] = useState(true);

  const availableDrilldownKeys = keys.filter((k) => k !== xKey && k !== yKey);

  // Drilldown keys selector
  const handleDrilldownKeysChange = (e) => {
    const selectedOptions = Array.from(e.target.selectedOptions).map((o) => o.value);
    setDrilldownKeys(selectedOptions);
  };

  // Prepare data for both modes
  const { series, drilldown, separateDrilldownData } = useMemo(() => {
    if (!xKey || !yKey) return { series: [], drilldown: undefined, separateDrilldownData: [] };

    const mainSeries = [];
    const drilldownSeries = [];
    const separateDrilldownData = [];

    data.forEach((row) => {
      const mainCategory = row[xKey];
      const yValue = Number(row[yKey]) || 0;

      mainSeries.push({
        name: mainCategory,
        y: yValue,
        drilldown: inlineDrilldown ? mainCategory : undefined,
      });

      const drilldownData = drilldownKeys.map((key) => [key, Number(row[key]) || 0]);

      drilldownSeries.push({
        id: mainCategory,
        name: `${mainCategory} Breakdown`,
        data: drilldownData,
      });

      // For separate chart, prepare data grouped by main category
      separateDrilldownData.push({
        category: mainCategory,
        data: drilldownData,
      });
    });

    return {
      series: [
        {
          name: yKey,
          colorByPoint: true,
          data: mainSeries,
        },
      ],
      drilldown: {
        series: drilldownSeries,
      },
      separateDrilldownData,
    };
  }, [data, xKey, yKey, drilldownKeys, inlineDrilldown]);

  // Options for main chart
  const mainOptions = {
    chart: { type: chartType },
    title: { text: title },
    xAxis: {
      type: "category",
      title: { text: xKey },
    },
    yAxis: {
      title: { text: yKey },
    },
    legend: { enabled: false },
    plotOptions: {
      series: {
        borderWidth: 0,
        dataLabels: { enabled: true },
      },
    },
    series,
    drilldown: inlineDrilldown && drilldownKeys.length ? drilldown : undefined,
  };

  // Options for separate drilldown chart
  // Default to show the first category's drilldown data
  const [selectedCategory, setSelectedCategory] = useState(
    separateDrilldownData[0]?.category || null
  );

  // When main data or drilldown keys change, reset selectedCategory
  React.useEffect(() => {
    setSelectedCategory(separateDrilldownData[0]?.category || null);
  }, [separateDrilldownData]);

  // Find drilldown data for selected category
  const selectedDrilldown = separateDrilldownData.find(
    (d) => d.category === selectedCategory
  );

  const separateDrilldownOptions = {
    chart: { type: "column" },
    title: { text: selectedCategory ? `${selectedCategory} Breakdown` : "Select category" },
    xAxis: {
      type: "category",
      title: { text: "Category" },
    },
    yAxis: {
      title: { text: "Value" },
    },
    legend: { enabled: false },
    plotOptions: {
      series: {
        borderWidth: 0,
        dataLabels: { enabled: true },
      },
    },
    series: [
      {
        name: "Details",
        colorByPoint: true,
        data: selectedDrilldown?.data || [],
      },
    ],
  };

  return (
    <div style={{ padding: "1rem", maxWidth: 900 }}>
      <h3>Create Chart with Drilldown</h3>

      <input
        placeholder="Chart Title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        style={{ marginBottom: 12, width: "100%", padding: 6, fontSize: 16 }}
      />

      <div style={{ display: "flex", gap: 12, flexWrap: "wrap", marginBottom: 12 }}>
        <select
          value={chartType}
          onChange={(e) => setChartType(e.target.value)}
          style={{ flex: 1, minWidth: 120, padding: 6 }}
        >
          {chartTypes.map((type) => (
            <option key={type} value={type}>
              {type}
            </option>
          ))}
        </select>

        <select
          value={xKey}
          onChange={(e) => {
            setXKey(e.target.value);
            setDrilldownKeys([]); // reset drilldown keys when xKey changes
          }}
          style={{ flex: 1, minWidth: 120, padding: 6 }}
        >
          {keys.map((k) => (
            <option key={k} value={k}>
              {k}
            </option>
          ))}
        </select>

        <select
          value={yKey}
          onChange={(e) => {
            setYKey(e.target.value);
            setDrilldownKeys([]); // reset drilldown keys when yKey changes
          }}
          style={{ flex: 1, minWidth: 180, padding: 6 }}
        >
          {keys.map((k) => (
            <option key={k} value={k}>
              {k}
            </option>
          ))}
        </select>

        <select
          multiple
          value={drilldownKeys}
          onChange={handleDrilldownKeysChange}
          style={{ flex: 1, minWidth: 250, padding: 6, height: 100 }}
          title="Select categories to show on drilldown (Ctrl/Cmd+click for multiple)"
        >
          {availableDrilldownKeys.map((k) => (
            <option key={k} value={k}>
              {k}
            </option>
          ))}
        </select>
      </div>

      {/* Toggle between inline drilldown and separate chart */}
      <div style={{ marginBottom: 16 }}>
        <label style={{ marginRight: 12 }}>
          <input
            type="radio"
            checked={inlineDrilldown}
            onChange={() => setInlineDrilldown(true)}
          />
          Inline Drilldown
        </label>
        <label>
          <input
            type="radio"
            checked={!inlineDrilldown}
            onChange={() => setInlineDrilldown(false)}
          />
          Separate Drilldown Chart
        </label>
      </div>

      <div style={{ display: "flex", gap: 20 }}>
        <div style={{ flex: 1, minWidth: 300 }}>
          <HighchartsReact highcharts={Highcharts} options={mainOptions} />
        </div>

        {!inlineDrilldown && (
          <div style={{ flex: 1, minWidth: 300 }}>
            {/* Dropdown to select category for separate drilldown */}
            <select
              value={selectedCategory || ""}
              onChange={(e) => setSelectedCategory(e.target.value)}
              style={{ marginBottom: 12, padding: 6, width: "100%" }}
            >
              {separateDrilldownData.map(({ category }) => (
                <option key={category} value={category}>
                  {category}
                </option>
              ))}
            </select>
            <HighchartsReact highcharts={Highcharts} options={separateDrilldownOptions} />
          </div>
        )}
      </div>

      <button
        onClick={() => onSave(title, JSON.stringify({ mainOptions, drilldownMode: inlineDrilldown ? "inline" : "separate" }, null, 2))}
        disabled={!xKey || !yKey}
        style={{ marginTop: 12, padding: "8px 16px", fontSize: 16, cursor: "pointer" }}
        title={!xKey || !yKey ? "Select X and Y axis keys first" : "Save Chart Config"}
      >
        Save Chart
      </button>
    </div>
  );
}
