import React, { useState, useMemo } from "react";
import HighchartsReact from "highcharts-react-official";
import Highcharts from "./utils/highcharts-config";

const chartTypes = ["column", "bar", "line", "pie", "area", "bubble"];

export default function ChartCreator({ data, onSave }) {
  const keys = Object.keys(data[0] || {});
  const [chartType, setChartType] = useState("column");
  const [xKey, setXKey] = useState(() => keys[0] || "");
  const [yKey, setYKey] = useState(() => keys[1] || "");
  const [zKey, setZKey] = useState(() => keys[2] || ""); // for bubble size
  const [drilldownKeys, setDrilldownKeys] = useState([]);
  const [title, setTitle] = useState("My Chart");

  const availableDrilldownKeys = keys.filter(
    (k) => k !== xKey && k !== yKey && k !== zKey
  );

  const handleDrilldownKeysChange = (e) => {
    const selectedOptions = Array.from(e.target.selectedOptions).map(
      (o) => o.value
    );
    setDrilldownKeys(selectedOptions);
  };

  const { series, drilldown } = useMemo(() => {
    if (!xKey || !yKey) return { series: [], drilldown: undefined };

    const mainSeries = [];
    const drilldownSeries = [];

    if (chartType === "bubble") {
      // Bubble requires x, y, and z values
      data.forEach((row) => {
        const xValue = Number(row[xKey]) || 0;
        const yValue = Number(row[yKey]) || 0;
        const zValue = Number(row[zKey]) || 0;
        const name = row[xKey] || "";

        mainSeries.push({
          name,
          x: xValue,
          y: yValue,
          z: zValue,
          drilldown: drilldownKeys.length ? name : undefined,
        });

        if (drilldownKeys.length) {
          const drilldownData = drilldownKeys.map((key) => [
            key,
            Number(row[key]) || 0,
          ]);

          drilldownSeries.push({
            id: name,
            name: `${name} Breakdown`,
            type: "column", // fallback for drilldown
            data: drilldownData,
          });
        }
      });
    } else {
      data.forEach((row) => {
        const mainCategory = row[xKey];
        const yValue = Number(row[yKey]) || 0;

        mainSeries.push({
          name: mainCategory,
          y: yValue,
          drilldown: drilldownKeys.length ? mainCategory : undefined,
        });

        if (drilldownKeys.length) {
          const drilldownData = drilldownKeys.map((key) => [
            key,
            Number(row[key]) || 0,
          ]);

          drilldownSeries.push({
            id: mainCategory,
            name: `${mainCategory} Breakdown`,
            type: "column",
            data: drilldownData,
          });
        }
      });
    }

    return {
      series: [
        {
          name: yKey,
          colorByPoint: true,
          type: chartType,
          data: mainSeries,
        },
      ],
      drilldown: drilldownSeries.length ? { series: drilldownSeries } : undefined,
    };
  }, [data, xKey, yKey, zKey, drilldownKeys, chartType]);

  // For bubble chart, allow selecting zKey (size)
  return (
    <div style={{ padding: "1rem", maxWidth: 700 }}>
      <h3>Create Chart with Drilldown</h3>

      <input
        placeholder="Chart Title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        style={{ marginBottom: 12, width: "100%", padding: 6, fontSize: 16 }}
      />

      <div
        style={{ display: "flex", gap: 12, flexWrap: "wrap", marginBottom: 12 }}
      >
        <select
          value={chartType}
          onChange={(e) => {
            setChartType(e.target.value);
            setDrilldownKeys([]);
          }}
          style={{ flex: 1, minWidth: 120, padding: 6 }}
        >
          {chartTypes.map((type) => (
            <option key={type} value={type}>
              {type}
            </option>
          ))}
        </select>

        <select
          value={xKey}
          onChange={(e) => {
            setXKey(e.target.value);
            setDrilldownKeys([]);
          }}
          style={{ flex: 1, minWidth: 120, padding: 6 }}
        >
          {keys.map((k) => (
            <option key={k} value={k}>
              {k}
            </option>
          ))}
        </select>

        <select
          value={yKey}
          onChange={(e) => {
            setYKey(e.target.value);
            setDrilldownKeys([]);
          }}
          style={{ flex: 1, minWidth: 180, padding: 6 }}
        >
          {keys.map((k) => (
            <option key={k} value={k}>
              {k}
            </option>
          ))}
        </select>

        {chartType === "bubble" && (
          <select
            value={zKey}
            onChange={(e) => setZKey(e.target.value)}
            style={{ flex: 1, minWidth: 180, padding: 6 }}
          >
            {keys.map((k) => (
              <option key={k} value={k}>
                {k}
              </option>
            ))}
          </select>
        )}

        <select
          multiple
          value={drilldownKeys}
          onChange={handleDrilldownKeysChange}
          style={{ flex: 1, minWidth: 250, padding: 6, height: 100 }}
          title="Select categories to show on drilldown (Ctrl/Cmd+click for multiple)"
        >
          {availableDrilldownKeys.map((k) => (
            <option key={k} value={k}>
              {k}
            </option>
          ))}
        </select>
      </div>

      <HighchartsReact highcharts={Highcharts} options={{ ...options, chart: { type: chartType }, series, drilldown, title: { text: title } }} />
    </div>
  );
}
