import React, { useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import MarkerClusterGroup from "react-leaflet-cluster";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

// Fix Leaflet default icon issue
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl:
    "https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon-2x.png",
  iconUrl: "https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png",
  shadowUrl: "https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png",
});

// Custom icons by school type
const iconAcademy = new L.Icon({
  iconUrl:
    "https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-green.png",
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowUrl:
    "https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png",
});

const iconComprehensive = new L.Icon({
  iconUrl:
    "https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-blue.png",
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowUrl:
    "https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png",
});

// Sample data
const schools = [
  {
    name: "Chelmsford County High School for Girls",
    type: "Academy",
    pupilsKS4: 151,
    progress8Score: 0.76,
    city: "Chelmsford",
    lat: 51.7356,
    lng: 0.4749,
  },
  {
    name: "Another School",
    type: "Comprehensive",
    pupilsKS4: 120,
    progress8Score: 0.5,
    city: "London",
    lat: 51.5074,
    lng: -0.1278,
  },
];

export default function SchoolsMap() {
  const [filterType, setFilterType] = useState("All");

  const filteredSchools =
    filterType === "All"
      ? schools
      : schools.filter((s) => s.type === filterType);

  const getIconByType = (type) => {
    switch (type) {
      case "Academy":
        return iconAcademy;
      case "Comprehensive":
        return iconComprehensive;
      default:
        return new L.Icon.Default();
    }
  };

  return (
    <div style={{ maxWidth: "900px", margin: "auto" }}>
      <h2>Schools Interactive Map</h2>

      <label>
        Filter by school type:{" "}
        <select
          value={filterType}
          onChange={(e) => setFilterType(e.target.value)}
          style={{ marginBottom: "1rem" }}
        >
          <option value="All">All</option>
          <option value="Academy">Academy</option>
          <option value="Comprehensive">Comprehensive</option>
        </select>
      </label>

      <MapContainer
        center={[52.0, -1.0]}
        zoom={6}
        scrollWheelZoom={true}
        style={{ height: "600px", width: "100%" }}
      >
        <TileLayer
          attribution='&copy; <a href="https://osm.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <MarkerClusterGroup chunkedLoading>
          {filteredSchools.map((school, idx) => (
            <Marker
              key={idx}
              position={[school.lat, school.lng]}
              icon={getIconByType(school.type)}
            >
              <Popup>
                <strong>{school.name}</strong>
                <br />
                Type: {school.type}
                <br />
                Pupils KS4: {school.pupilsKS4}
                <br />
                Progress 8 Score: {school.progress8Score}
                <br />
                City: {school.city}
              </Popup>
            </Marker>
          ))}
        </MarkerClusterGroup>
      </MapContainer>
    </div>
  );
}
