export default function DashboardPage() {
  const categories = mainData.map((d) => d.Year);

  // Main series for line chart, with drilldown link to year
  const totalBudgetSeries = mainData.map((d) => ({
    y: d.TotalBudget,
    drilldown: d.Year,
  }));

  // Create drilldown series for each year
  const drilldownSeries = Object.entries(drilldownData).map(([year, data]) => ({
    id: year,
    name: `Departmental Budgets for ${year}`,
    type: "bar",
    categories: data.categories,
    data: [
      {
        name: "Resource Budget",
        data: data.resource,
        color: "#7cb5ec",
      },
      {
        name: "Capital Budget",
        data: data.capital,
        color: "#434348",
      },
    ],
    // Highcharts expects an array of points, so we’ll need to organize the data properly below
  }));

  // Highcharts expects drilldown series data as array of points,
  // but to show grouped bars, we need to create multiple series for each drilldown.
  // So we need to split drilldown into 2 series per year (resource and capital) with matching categories.

  // Rebuild drilldown series to split into two series per drilldown id:

  const drilldownSeriesExpanded = [];

  Object.entries(drilldownData).forEach(([year, data]) => {
    drilldownSeriesExpanded.push({
      id: year + "-resource",
      name: `Resource Budget for ${year}`,
      type: "bar",
      data: data.resource.map((val, i) => [data.categories[i], val]),
      color: "#7cb5ec",
    });
    drilldownSeriesExpanded.push({
      id: year + "-capital",
      name: `Capital Budget for ${year}`,
      type: "bar",
      data: data.capital.map((val, i) => [data.categories[i], val]),
      color: "#434348",
    });
  });

  // Modify main series data to drilldown into both resource and capital series for each year,
  // by setting the drilldown to an array? Highcharts drilldown only supports a single drilldown id,
  // so instead we’ll use a trick: when user clicks a year, drilldown will show *combined* resource + capital bars as grouped series.

  // We'll create a *combined* drilldown per year with two series (resource and capital).

  // So drilldownSeriesExpanded already does that, but how to call both at once?

  // Highcharts drilldown supports multiple series per drilldown ID, so we just assign the same id to both.

  // Fix that:

  const drilldownSeriesGrouped = [];

  Object.entries(drilldownData).forEach(([year, data]) => {
    drilldownSeriesGrouped.push({
      id: year,
      name: `Resource Budget for ${year}`,
      type: "bar",
      data: data.resource.map((val, i) => [data.categories[i], val]),
      color: "#7cb5ec",
      showInLegend: true,
    });
    drilldownSeriesGrouped.push({
      id: year,
      name: `Capital Budget for ${year}`,
      type: "bar",
      data: data.capital.map((val, i) => [data.categories[i], val]),
      color: "#434348",
      showInLegend: true,
    });
  });

  const options = {
    chart: {
      type: "line",
      height: 550,
    },
    title: {
      text: "Total Budget Over Years (£M) - Click points for details",
    },
    xAxis: {
      categories,
      title: { text: "Year" },
    },
    yAxis: {
      min: 0,
      title: { text: "Budget (£M)" },
      labels: {
        formatter() {
          return `£${this.value}M`;
        },
      },
    },
    tooltip: {
      shared: true,
      valuePrefix: "£",
      valueSuffix: "M",
    },
    plotOptions: {
      series: {
        cursor: "pointer",
        marker: {
          enabled: true,
        },
      },
      bar: {
        grouping: true,
      },
    },
    legend: {
      enabled: true,
    },
    series: [
      {
        name: "Total Budget",
        data: totalBudgetSeries,
        color: "#007bff",
      },
    ],
    drilldown: {
      series: drilldownSeriesGrouped,
    },
  };

  return (
    <div style={{ maxWidth: 1000, margin: "auto", padding: 20 }}>
      <HighchartsReact highcharts={Highcharts} options={options} />
      <p style={{ marginTop: 20, fontStyle: "italic" }}>
        Click on a year point in the line chart to see departmental Resource and Capital budget breakdowns.
      </p>
      <p style={{ fontSize: "smaller" }}>
        Resource Budget bars are blue; Capital Budget bars are dark gray.
      </p>
    </div>
  );
}
