// frontend-react/src/services/api.js
import axios from "axios";

const API_URL = "http://localhost:8080/api";

export const uploadExcel = (file, sheet, range) => {
  const formData = new FormData();
  formData.append("file", file);
  formData.append("sheet", sheet);
  formData.append("range", range);

  return axios.post(`${API_URL}/upload/excel`, formData);
};

export const uploadCsv = (file) => {
  const formData = new FormData();
  formData.append("file", file);

  return axios.post(`${API_URL}/upload/csv`, formData);
};

export const saveChart = (name, configJson) =>
  axios.post(`${API_URL}/chart/save`, { name, configJson });

export const getCharts = () => axios.get(`${API_URL}/chart/all`);
