// frontend-react/src/services/api.js
import axios from "axios";

const API_URL = "http://localhost:8080/api";

export const uploadExcel = (file, sheet, range) => {
  const formData = new FormData();
  formData.append("file", file);
  formData.append("sheet", sheet);
  formData.append("range", range);

  return axios
    .post(`${API_URL}/upload/excel`, formData)
    .then((res) => {
      console.log("Excel upload response:", res);
      return res;
    })
    .catch((err) => {
      console.error("Excel upload error:", err);
      throw err;
    });
};

export const uploadCsv = (file) => {
  const formData = new FormData();
  formData.append("file", file);

  return axios
    .post(`${API_URL}/upload/csv`, formData)
    .then((res) => {
      console.log("CSV upload response:", res);
      return res;
    })
    .catch((err) => {
      console.error("CSV upload error:", err);
      throw err;
    });
};

export const saveChart = (name, configJson) => {
  return axios
    .post(`${API_URL}/chart/save`, { name, configJson })
    .then((res) => {
      console.log("Save chart response:", res);
      return res;
    })
    .catch((err) => {
      console.error("Save chart error:", err);
      throw err;
    });
};

export const getCharts = () => {
  return axios
    .get(`${API_URL}/chart/all`)
    .then((res) => {
      console.log("Get charts response:", res);
      return res;
    })
    .catch((err) => {
      console.error("Get charts error:", err);
      throw err;
    });
};

