import React, { useEffect, useRef, useState } from "react";
import maplibregl from "maplibre-gl";
import "maplibre-gl/dist/maplibre-gl.css";

// Sample data
const schools = [
  {
    name: "Chelmsford County High School for Girls",
    type: "Academy",
    pupilsKS4: 151,
    progress8Score: 0.76,
    city: "Chelmsford",
    lat: 51.7356,
    lng: 0.4749,
  },
  {
    name: "Another School",
    type: "Comprehensive",
    pupilsKS4: 120,
    progress8Score: 0.5,
    city: "London",
    lat: 51.5074,
    lng: -0.1278,
  },
];

export default function SchoolsMap() {
  const [filterType, setFilterType] = useState("All");
  const mapContainer = useRef(null);
  const map = useRef(null);

  const filteredSchools =
    filterType === "All"
      ? schools
      : schools.filter((s) => s.type === filterType);

  useEffect(() => {
    if (map.current) return;

    // Initialize MapLibre map
    map.current = new maplibregl.Map({
      container: mapContainer.current,
      style: "https://demotiles.maplibre.org/style.json", // Open public vector tiles
      center: [0, 52],
      zoom: 5,
    });

    // Add zoom + rotation controls
    map.current.addControl(new maplibregl.NavigationControl(), "top-right");
  }, []);

  useEffect(() => {
    if (!map.current) return;

    // Clear existing markers
    const markers = [];
    filteredSchools.forEach((school) => {
      const el = document.createElement("div");
      el.className = "marker";
      el.style.width = "25px";
      el.style.height = "25px";
      el.style.backgroundColor =
        school.type === "Academy" ? "green" : "blue";
      el.style.borderRadius = "50%";

      const marker = new maplibregl.Marker(el)
        .setLngLat([school.lng, school.lat])
        .setPopup(
          new maplibregl.Popup().setHTML(`
            <strong>${school.name}</strong><br/>
            Type: ${school.type}<br/>
            Pupils KS4: ${school.pupilsKS4}<br/>
            Progress 8 Score: ${school.progress8Score}<br/>
            City: ${school.city}
          `)
        )
        .addTo(map.current);

      markers.push(marker);
    });

    // Cleanup on re-render
    return () => {
      markers.forEach((m) => m.remove());
    };
  }, [filteredSchools]);

  return (
    <div style={{ maxWidth: "900px", margin: "auto" }}>
      <h2>Schools Interactive Map (MapLibre)</h2>

      <label>
        Filter by school type:{" "}
        <select
          value={filterType}
          onChange={(e) => setFilterType(e.target.value)}
          style={{ marginBottom: "1rem" }}
        >
          <option value="All">All</option>
          <option value="Academy">Academy</option>
          <option value="Comprehensive">Comprehensive</option>
        </select>
      </label>

      <div
        ref={mapContainer}
        style={{ height: "600px", width: "100%", borderRadius: "8px" }}
      />
    </div>
  );
}
