// src/components/ChartCreator.jsx
import React, { useState } from "react";
import HighchartsReact from "highcharts-react-official";
import Highcharts from "../utils/highcharts-config"; // using your custom Highcharts with drilldown

const chartTypes = [
  "line", "bar", "column", "pie", "scatter", "area", "bubble", "spline", "areaspline"
];

export default function ChartCreator({ data, onSave }) {
  const [chartType, setChartType] = useState("column");
  const [xKey, setXKey] = useState("");
  const [yKey, setYKey] = useState("");
  const [drilldownKey, setDrilldownKey] = useState(""); // key for drilldown
  const [title, setTitle] = useState("My Chart");

  const keys = data.length > 0 ? Object.keys(data[0]) : [];

  const buildSeriesWithDrilldown = () => {
    if (!xKey || !yKey) return [];

    // Group main series
    const grouped = {};
    const drilldownSeries = [];

    data.forEach(row => {
      const main = row[xKey];
      const sub = row[drilldownKey];
      const yVal = row[yKey];

      if (!grouped[main]) grouped[main] = { total: 0, drill: {} };

      grouped[main].total += yVal;

      if (sub) {
        grouped[main].drill[sub] = (grouped[main].drill[sub] || 0) + yVal;
      }
    });

    const mainSeries = Object.entries(grouped).map(([name, { total, drill }]) => ({
      name,
      y: total,
      drilldown: Object.keys(drill).length ? name : null
    }));

    Object.entries(grouped).forEach(([name, { drill }]) => {
      if (Object.keys(drill).length) {
        drilldownSeries.push({
          id: name,
          name: `${name} details`,
          data: Object.entries(drill)
        });
      }
    });

    return {
      series: [{
        name: yKey,
        data: mainSeries
      }],
      drilldown: {
        series: drilldownSeries
      }
    };
  };

  const { series, drilldown } = buildSeriesWithDrilldown();

  const options = {
    chart: { type: chartType },
    title: { text: title },
    xAxis: { type: 'category', title: { text: xKey } },
    yAxis: { title: { text: yKey } },
    legend: { enabled: true },
    plotOptions: {
      series: {
        borderWidth: 0,
        dataLabels: { enabled: true }
      }
    },
    series,
    drilldown
  };

  const handleSave = () => {
    const configJson = JSON.stringify(options);
    onSave(title, configJson);
  };

  return (
    <div style={{ padding: "1rem" }}>
      <h3>Create Chart</h3>

      <input
        placeholder="Chart Title"
        value={title}
        onChange={e => setTitle(e.target.value)}
        style={{ marginBottom: 8 }}
      />

      <div style={{ display: "flex", gap: 10, flexWrap: "wrap", marginBottom: 8 }}>
        <select value={chartType} onChange={e => setChartType(e.target.value)}>
          {chartTypes.map(type => (
            <option key={type} value={type}>{type}</option>
          ))}
        </select>

        <select value={xKey} onChange={e => setXKey(e.target.value)}>
          <option value="">X-axis</option>
          {keys.map(k => <option key={k} value={k}>{k}</option>)}
        </select>

        <select value={yKey} onChange={e => setYKey(e.target.value)}>
          <option value="">Y-axis</option>
          {keys.map(k => <option key={k} value={k}>{k}</option>)}
        </select>

        <select value={drilldownKey} onChange={e => setDrilldownKey(e.target.value)}>
          <option value="">Drilldown Key (Optional)</option>
          {keys.map(k => <option key={k} value={k}>{k}</option>)}
        </select>
      </div>

      <HighchartsReact highcharts={Highcharts} options={options} />

      <button onClick={handleSave} style={{ marginTop: 10 }}>Save Chart</button>
    </div>
  );
}
