const DashboardPage = () => {
  const [chartOptions, setChartOptions] = useState({
    chart: {
      type: "column",
    },
    title: {
      text: "Northern Ireland Departmental Budget and Spend",
      style: {
        fontWeight: "bold",
        fontSize: "20px",
        color: "white",
        textOutline: "2px black",
      },
    },
    subtitle: {
      text: "Click on a year to view detailed departmental budgets",
      style: {
        color: "white",
      },
    },
    legend: {
      enabled: true,
      itemStyle: { color: "white" },
      itemHoverStyle: { color: "#999" },
    },
    xAxis: {
      categories: mainData.map((item) => item.Year),
      title: {
        text: "Year",
        style: { color: "white" },
      },
      labels: {
        style: { color: "white" },
      },
      crosshair: true,
    },
    yAxis: {
      min: 0,
      title: {
        text: "Budget (£ millions)",
        style: { color: "white" },
      },
      labels: {
        style: { color: "white" },
      },
      gridLineColor: "rgba(255,255,255,0.2)",
    },
    tooltip: {
      shared: true,
      backgroundColor: "#2c3e50",
      style: { color: "white" },
      formatter: function () {
        let s = `<b>Year: ${this.x}</b>`;
        this.points.forEach((point) => {
          s += `<br/><span style="color:${point.color}">\u25CF</span> ${point.series.name}: <b>£${point.y.toLocaleString()}</b>`;
        });
        return s;
      },
    },
    plotOptions: {
      column: {
        stacking: false,
        dataLabels: {
          enabled: false,
        },
      },
      series: {
        cursor: "pointer",
        point: {
          events: {
            click: function () {
              const year = this.category;
              if (!drilldownData[year]) return;
              // Drilldown to show detailed departments for the clicked year
              setChartOptions((prev) => ({
                ...prev,
                title: {
                  text: `Detailed Departmental Budgets for ${year}`,
                  style: prev.title.style,
                },
                subtitle: {
                  text: `<a href="#" style="color:#fff; text-decoration: underline;">Back to overview</a>`,
                  useHTML: true,
                  style: { color: "white" },
                },
                xAxis: {
                  categories: drilldownData[year].categories,
                  title: { text: "Department", style: { color: "white" } },
                  labels: { style: { color: "white" } },
                },
                yAxis: {
                  title: { text: "Budget (£ millions)", style: { color: "white" } },
                  labels: { style: { color: "white" } },
                },
                series: [
                  {
                    name: "Resource Budget",
                    data: drilldownData[year].resource,
                    color: "#7cb5ec",
                  },
                  {
                    name: "Capital Budget",
                    data: drilldownData[year].capital,
                    color: "#434348",
                  },
                ],
              }));
            },
          },
        },
      },
    },
    series: [
      {
        name: "Total Resource",
        data: mainData.map((item) => item.TotalResource),
        color: "#7cb5ec",
      },
      {
        name: "Total Capital",
        data: mainData.map((item) => item.TotalCapital),
        color: "#434348",
      },
    ],
    credits: {
      enabled: false,
    },
    backgroundColor: "#1e2a38",
  });

  // Handle subtitle link click to reset chart
  const handleSubtitleClick = (e) => {
    if (
      e.target.tagName === "A" &&
      e.target.textContent.toLowerCase().includes("back to overview")
    ) {
      e.preventDefault();
      setChartOptions((prev) => ({
        ...prev,
        title: {
          text: "Northern Ireland Departmental Budget and Spend",
          style: prev.title.style,
        },
        subtitle: {
          text: "Click on a year to view detailed departmental budgets",
          style: { color: "white" },
        },
        xAxis: {
          categories: mainData.map((item) => item.Year),
          title: { text: "Year", style: { color: "white" } },
          labels: { style: { color: "white" } },
        },
        yAxis: {
          title: { text: "Budget (£ millions)", style: { color: "white" } },
          labels: { style: { color: "white" } },
        },
        series: [
          {
            name: "Total Resource",
            data: mainData.map((item) => item.TotalResource),
            color: "#7cb5ec",
          },
          {
            name: "Total Capital",
            data: mainData.map((item) => item.TotalCapital),
            color: "#434348",
          },
        ],
      }));
    }
  };

  return (
    <div
      style={{
        padding: "20px",
        backgroundColor: "#1e2a38",
        minHeight: "100vh",
        color: "white",
      }}
      onClick={handleSubtitleClick}
    >
      <h1 style={{ marginBottom: "10px" }}>
        <Link to="/" style={{ color: "white", textDecoration: "none" }}>
          Home
        </Link>{" "}
        / Dashboard
      </h1>

      <HighchartsReact highcharts={Highcharts} options={chartOptions} />
    </div>
  );
};

export default DashboardPage;
