// src/components/utils/chartStyles.js

const chartStyles = {
  chart: {
    animation: { duration: 800, easing: "easeOutBounce" },
  },
  title: {
    style: { fontSize: "22px", fontWeight: "600", color: "#333" },
  },
  xAxis: {
    type: "category",
    title: { style: { fontWeight: "600", color: "#666" } },
    gridLineWidth: 1,
    gridLineColor: "#e6e6e6",
    labels: { style: { fontSize: "13px", color: "#444" } },
  },
  yAxis: {
    title: { style: { fontWeight: "600", color: "#666" } },
    gridLineWidth: 1,
    gridLineColor: "#e6e6e6",
    labels: { style: { fontSize: "13px", color: "#444" } },
  },
  legend: { enabled: true },
  plotOptions: {
    series: {
      borderWidth: 1,
      borderColor: "#ddd",
      shadow: true,
      dataLabels: {
        enabled: true,
        style: {
          fontSize: "13px",
          color: "#222",
          fontWeight: "600",
          textOutline: "none",
        },
      },
      animation: { duration: 600 },
    },
    pie: {
      allowPointSelect: true,
      cursor: "pointer",
      dataLabels: {
        enabled: true,
        format: "<b>{point.name}</b>: {point.percentage:.1f} %",
        style: {
          fontSize: "14px",
          fontWeight: "600",
          color: "#444",
          textOutline: "none",
        },
      },
      shadow: true,
      borderColor: "#fff",
      borderWidth: 2,
      animation: { duration: 700 },
    },
  },
  tooltip: {
    backgroundColor: "#fff",
    borderColor: "#bbb",
    borderRadius: 6,
    borderWidth: 1,
    shadow: true,
    style: { fontSize: "14px", color: "#333" },
    pointFormat: "<b>{point.y}</b> ({point.percentage:.1f}%)",
  },
};

export default chartStyles;
