// src/components/ChartCreator.jsx
import React, { useState, useMemo } from "react";
import HighchartsReact from "highcharts-react-official";
import Highcharts from "./utils/highcharts-config";

const chartTypes = ["column", "bar", "line", "pie", "area"];

export default function ChartCreator({ data, onSave }) {
  const keys = Object.keys(data[0] || {});
  const [chartType, setChartType] = useState("column");
  const [xKey, setXKey] = useState(() => keys[0] || "");
  const [yKeys, setYKeys] = useState(() => (keys.length > 1 ? [keys[1]] : []));
  const [drilldownKeys, setDrilldownKeys] = useState([]);
  const [title, setTitle] = useState("My Chart");

  // Available drilldown keys exclude xKey and all selected yKeys
  const availableDrilldownKeys = keys.filter(
    (k) => k !== xKey && !yKeys.includes(k)
  );

  // Drilldown keys selector handler
  const handleDrilldownKeysChange = (e) => {
    const selectedOptions = Array.from(e.target.selectedOptions).map(
      (o) => o.value
    );
    setDrilldownKeys(selectedOptions);
  };

  const { series, drilldown } = useMemo(() => {
    if (!xKey || yKeys.length === 0) return { series: [], drilldown: undefined };

    // Create one series per selected yKey
    const mainSeries = yKeys.map((yKey) => ({
      name: yKey,
      colorByPoint: true,
      data: data.map((row) => ({
        name: row[xKey],
        y: Number(row[yKey]) || 0,
        drilldown: row[xKey],
      })),
    }));

    // Create drilldown series
    const drilldownSeries = drilldownKeys.map((key) => ({
      id: key,
      name: `${key} Breakdown`,
      data: data.map((row) => [row[xKey], Number(row[key]) || 0]),
    }));

    return {
      series: mainSeries,
      drilldown: { series: drilldownSeries },
    };
  }, [data, xKey, yKeys, drilldownKeys]);

  const options = {
    chart: { type: chartType },
    title: { text: title },
    xAxis: {
      type: "category",
      title: { text: xKey },
    },
    yAxis: {
      title: { text: yKeys.join(", ") },
    },
    legend: { enabled: true },
    plotOptions: {
      series: {
        borderWidth: 0,
        dataLabels: { enabled: true },
      },
    },
    series,
    drilldown,
  };

  return (
    <div style={{ padding: "1rem", maxWidth: 700 }}>
      <h3>Create Chart with Drilldown</h3>

      <input
        placeholder="Chart Title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        style={{ marginBottom: 12, width: "100%", padding: 6, fontSize: 16 }}
      />

      <div
        style={{
          display: "flex",
          gap: 12,
          flexWrap: "wrap",
          marginBottom: 12,
          alignItems: "flex-start",
        }}
      >
        <select
          value={chartType}
          onChange={(e) => setChartType(e.target.value)}
          style={{ flex: 1, minWidth: 120, padding: 6 }}
        >
          {chartTypes.map((type) => (
            <option key={type} value={type}>
              {type}
            </option>
          ))}
        </select>

        <select
          value={xKey}
          onChange={(e) => {
            setXKey(e.target.value);
            setDrilldownKeys([]); // reset drilldown keys when xKey changes
          }}
          style={{ flex: 1, minWidth: 120, padding: 6 }}
        >
          {keys.map((k) => (
            <option key={k} value={k}>
              {k}
            </option>
          ))}
        </select>

        <select
          multiple
          value={yKeys}
          onChange={(e) =>
            setYKeys(Array.from(e.target.selectedOptions, (o) => o.value))
          }
          style={{ flex: 1, minWidth: 180, padding: 6, height: 100 }}
          title="Select one or more metrics (Ctrl/Cmd+click for multiple)"
        >
          {keys.map((k) => (
            <option key={k} value={k}>
              {k}
            </option>
          ))}
        </select>

        <select
          multiple
          value={drilldownKeys}
          onChange={handleDrilldownKeysChange}
          style={{ flex: 1, minWidth: 250, padding: 6, height: 100 }}
          title="Select categories to show on drilldown (Ctrl/Cmd+click for multiple)"
        >
          {availableDrilldownKeys.map((k) => (
            <option key={k} value={k}>
              {k}
            </option>
          ))}
        </select>
      </div>

      <HighchartsReact highcharts={Highcharts} options={options} />
    </div>
  );
}
