import React, { useState, useMemo } from "react";
import HighchartsReact from "highcharts-react-official";
import Highcharts from "./utils/highcharts-config";

const chartTypes = ["column", "bar", "line", "area"]; // 'pie' removed because it's incompatible with multiple series

export default function ChartCreator({ data, onSave }) {
  const keys = Object.keys(data[0] || {});
  const [chartType, setChartType] = useState("column");
  const [xKey, setXKey] = useState(() => keys[0] || "");
  const [yKeys, setYKeys] = useState(() => (keys[1] ? [keys[1]] : []));
  const [drilldownKeys, setDrilldownKeys] = useState([]);
  const [title, setTitle] = useState("My Chart");

  const availableYKeys = keys.filter((k) => k !== xKey);
  const availableDrilldownKeys = keys.filter((k) => k !== xKey && !yKeys.includes(k));

  const handleDrilldownKeysChange = (e) => {
    const selectedOptions = Array.from(e.target.selectedOptions).map((o) => o.value);
    setDrilldownKeys(selectedOptions);
  };

  const handleYKeysChange = (e) => {
    const selected = Array.from(e.target.selectedOptions).map((o) => o.value);
    setYKeys(selected);
    setDrilldownKeys([]); // reset drilldown when metrics change
  };

  const { series, drilldown } = useMemo(() => {
    if (!xKey || yKeys.length === 0) return { series: [], drilldown: undefined };

    const mainSeries = yKeys.map((metric) => ({
      name: metric,
      data: data.map((row) => [row[xKey], Number(row[metric]) || 0]),
    }));

    const drilldownSeries = data.map((row) => ({
      id: row[xKey],
      name: `${row[xKey]} Breakdown`,
      data: drilldownKeys.map((key) => [key, Number(row[key]) || 0]),
    }));

    return {
      series: mainSeries,
      drilldown: {
        series: drilldownSeries,
      },
    };
  }, [data, xKey, yKeys, drilldownKeys]);

  const options = {
    chart: { type: chartType },
    title: { text: title },
    xAxis: {
      type: "category",
      title: { text: xKey },
    },
    yAxis: {
      title: { text: yKeys.join(", ") },
    },
    legend: { enabled: true },
    plotOptions: {
      series: {
        borderWidth: 0,
        dataLabels: { enabled: true },
      },
    },
    series,
    drilldown,
  };

  // Optional: second summary chart showing totals per drilldownKey
  const secondChartSeries = useMemo(() => {
    if (drilldownKeys.length === 0) return [];
    return drilldownKeys.map((key) => {
      const total = data.reduce((sum, row) => sum + (Number(row[key]) || 0), 0);
      return { name: key, y: total };
    });
  }, [data, drilldownKeys]);

  const secondChartOptions = {
    chart: { type: "pie" },
    title: { text: "Summary of Selected Drilldown Keys" },
    series: [
      {
        name: "Total",
        colorByPoint: true,
        data: secondChartSeries,
      },
    ],
  };

  return (
    <div style={{ padding: "1rem", maxWidth: 800 }}>
      <h3>Create Chart with Multiple Metrics</h3>

      <input
        placeholder="Chart Title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        style={{ marginBottom: 12, width: "100%", padding: 6, fontSize: 16 }}
      />

      <div style={{ display: "flex", gap: 12, flexWrap: "wrap", marginBottom: 12 }}>
        <select
          value={chartType}
          onChange={(e) => setChartType(e.target.value)}
          style={{ flex: 1, minWidth: 120, padding: 6 }}
        >
          {chartTypes.map((type) => (
            <option key={type} value={type}>
              {type}
            </option>
          ))}
        </select>

        <select
          value={xKey}
          onChange={(e) => {
            setXKey(e.target.value);
            setDrilldownKeys([]);
          }}
          style={{ flex: 1, minWidth: 120, padding: 6 }}
        >
          {keys.map((k) => (
            <option key={k} value={k}>
              {k}
            </option>
          ))}
        </select>

        <select
          multiple
          value={yKeys}
          onChange={handleYKeysChange}
          style={{ flex: 1, minWidth: 200, padding: 6, height: 100 }}
          title="Select one or more Y metrics"
        >
          {availableYKeys.map((k) => (
            <option key={k} value={k}>
              {k}
            </option>
          ))}
        </select>

        <select
          multiple
          value={drilldownKeys}
          onChange={handleDrilldownKeysChange}
          style={{ flex: 1, minWidth: 250, padding: 6, height: 100 }}
          title="Select categories to show on drilldown"
        >
          {availableDrilldownKeys.map((k) => (
            <option key={k} value={k}>
              {k}
            </option>
          ))}
        </select>
      </div>

      {/* Main Chart */}
      <HighchartsReact highcharts={Highcharts} options={options} />

      {/* Second Summary Chart */}
      {secondChartSeries.length > 0 && (
        <div style={{ marginTop: 40 }}>
          <HighchartsReact highcharts={Highcharts} options={secondChartOptions} />
        </div>
      )}
    </div>
  );
}
