import React, { useState } from "react";
import { uploadExcel, uploadCsv, getSheetNames } from "../services/api";
import ChartCreator from "./ChartCreator";

export default function FileUploader() {
  const [files, setFiles] = useState([]); // array of selected files
  const [sheetsPerFile, setSheetsPerFile] = useState({}); // sheets for each file by index
  const [selectedSheets, setSelectedSheets] = useState({}); // selected sheet index per file
  const [ranges, setRanges] = useState({}); // range string per file
  const [fileType, setFileType] = useState("excel");
  const [data, setData] = useState(null);
  const [showDataPreview, setShowDataPreview] = useState(false);
  const [showUploadOptions, setShowUploadOptions] = useState(true);

  // Handle multiple file selection
  const handleFilesChange = async (e) => {
    const selectedFiles = Array.from(e.target.files);
    setFiles(selectedFiles);
    setData(null);
    setShowDataPreview(false);
    setSheetsPerFile({});
    setSelectedSheets({});
    setRanges({});

    if (fileType === "excel") {
      const sheetsMap = {};
      const selectedSheetsInit = {};
      const rangesInit = {};

      for (let i = 0; i < selectedFiles.length; i++) {
        try {
          const res = await getSheetNames(selectedFiles[i]);
          sheetsMap[i] = res.data.sheets || [];
          selectedSheetsInit[i] = 0;
          rangesInit[i] = "A3:Q19";
        } catch {
          sheetsMap[i] = [];
          selectedSheetsInit[i] = 0;
          rangesInit[i] = "A3:Q19";
        }
      }
      setSheetsPerFile(sheetsMap);
      setSelectedSheets(selectedSheetsInit);
      setRanges(rangesInit);
    }
  };

  // Sheet select handler for file i
  const handleSheetSelect = (fileIndex, sheetIndex) => {
    setSelectedSheets((prev) => ({ ...prev, [fileIndex]: sheetIndex }));
  };

  // Range input handler for file i
  const handleRangeChange = (fileIndex, newRange) => {
    setRanges((prev) => ({ ...prev, [fileIndex]: newRange }));
  };

  // Upload all files sequentially, combine data arrays
  const handleUploadAll = async () => {
    if (files.length === 0) return;

    let combinedData = [];

    try {
      for (let i = 0; i < files.length; i++) {
        let res;
        if (fileType === "excel") {
          res = await uploadExcel(files[i], selectedSheets[i], ranges[i]);
        } else {
          res = await uploadCsv(files[i]);
        }
        combinedData = combinedData.concat(res.data);
      }
      setData(combinedData);
      setShowDataPreview(true);
    } catch (err) {
      alert("Upload failed");
      console.error(err);
    }
  };

  return (
    <div style={{ padding: "1rem" }}>
      <h3
        onClick={() => setShowUploadOptions((prev) => !prev)}
        style={{ cursor: "pointer" }}
      >
        {showUploadOptions ? "▼" : "▶"} Upload Data File(s)
      </h3>

      {showUploadOptions && (
        <div style={{ marginBottom: 16 }}>
          <div>
            <label>
              <strong>File Type:</strong>
            </label>
            <select
              value={fileType}
              onChange={(e) => {
                setFileType(e.target.value);
                setFiles([]);
                setData(null);
                setSheetsPerFile({});
                setSelectedSheets({});
                setRanges({});
              }}
              style={{ marginLeft: 8 }}
            >
              <option value="excel">Excel</option>
              <option value="csv">CSV</option>
            </select>
          </div>

          <div style={{ marginTop: 8 }}>
            <input
              type="file"
              multiple
              onChange={handleFilesChange}
              accept={fileType === "excel" ? ".xlsx,.xls" : ".csv"}
            />
          </div>

          {/* For each file, show sheets and range selectors */}
          {files.map((file, i) => (
            <div
              key={i}
              style={{
                marginTop: 12,
                padding: 10,
                border: "1px solid #ccc",
                borderRadius: 4,
                backgroundColor: "#f9f9f9",
              }}
            >
              <div>
                <strong>File {i + 1}:</strong> {file.name}
              </div>

              {fileType === "excel" && sheetsPerFile[i]?.length > 0 && (
                <>
                  <div style={{ marginTop: 8 }}>
                    <label>
                      Sheet:{" "}
                      <select
                        value={selectedSheets[i] || 0}
                        onChange={(e) =>
                          handleSheetSelect(i, Number(e.target.value))
                        }
                      >
                        {sheetsPerFile[i].map((sheetName, idx) => (
                          <option key={idx} value={idx}>
                            {sheetName}
                          </option>
                        ))}
                      </select>
                    </label>
                  </div>
                  <div style={{ marginTop: 8 }}>
                    <label>
                      Range:{" "}
                      <input
                        type="text"
                        value={ranges[i] || ""}
                        onChange={(e) => handleRangeChange(i, e.target.value)}
                        placeholder="e.g. A3:Q19"
                        style={{ width: 100 }}
                      />
                    </label>
                  </div>
                </>
              )}
            </div>
          ))}

          <div style={{ marginTop: 16 }}>
            <button onClick={handleUploadAll}>Upload & Preview All</button>
          </div>
        </div>
      )}

      {data && (
        <>
          <div style={{ marginTop: 16 }}>
            <label>
              <input
                type="checkbox"
                checked={showDataPreview}
                onChange={() => setShowDataPreview((prev) => !prev)}
              />{" "}
              Show Data Table
            </label>
          </div>

          {showDataPreview && (
            <div style={{ overflowX: "auto", marginTop: 8 }}>
              <h4>Data Preview (Combined)</h4>
              <table border="1" cellPadding={5}>
                <thead>
                  <tr>
                    {Object.keys(data[0]).map((key) => (
                      <th key={key}>{key}</th>
                    ))}
                  </tr>
                </thead>
                <tbody>
                  {data.map((row, i) => (
                    <tr key={i}>
                      {Object.values(row).map((val, j) => (
                        <td key={j}>{val}</td>
                      ))}
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          <div style={{ marginTop: 16 }}>
            <ChartCreator data={data} />
          </div>
        </>
      )}
    </div>
  );
}
