import React, { useState } from "react";
import FileUploader from "./components/FileUploader";
import ChartCreator from "./components/ChartCreator";
//import ChartList from "./components/ChartList";
import logo from './components/assets/png/FIT.png';
import logo1 from './components/assets/png/FA.png';

function App() {
  const [loadedData, setLoadedData] = useState(null);

  return (
    <div
      style={{
        maxWidth: 1000,
        margin: "0 auto",
        padding: "20px",
        fontFamily: "'Segoe UI', sans-serif",
        color: "#333",
      }}
    >
      <header style={{ textAlign: "center", marginBottom: 30 }}>
        <div className="flex items-center gap-4">
          <img src={logo} alt="FIT Logo" className="w-20 h-20 object-contain" />
          <img src={logo1} alt="FA Logo" className="w-20 h-20 object-contain" />
        </div>
        <p style={{ color: "#666", fontSize: "0.95rem" }}>
          Upload your data and create interactive charts
        </p>
      </header>

      <section
        style={{
          border: "1px solid #ddd",
          borderRadius: 8,
          padding: 20,
          marginBottom: 30,
          backgroundColor: "#fafafa",
        }}
      >
        <h2 style={{ fontSize: "1.2rem", marginBottom: 10 }}>1Ô∏è‚É£ Upload Files</h2>
        <label style={{ fontWeight: "bold", marginBottom: 8, display: "block" }}>
          Supported formats: <span style={{ fontWeight: "normal" }}>Excel (.xlsx) and CSV</span>
        </label>
        <FileUploader onDataLoaded={setLoadedData} />
      </section>

      {loadedData && (
        <section
          style={{
            border: "1px solid #ddd",
            borderRadius: 8,
            padding: 20,
            marginBottom: 30,
            backgroundColor: "#fff",
          }}
        >
          <h2 style={{ fontSize: "1.2rem", marginBottom: 10 }}>
            2Ô∏è‚É£ Create Your Chart
          </h2>
          <ChartCreator data={loadedData} />
        </section>
      )}

      {/* <section>
        <h2 style={{ fontSize: "1.2rem", marginBottom: 10 }}>üìã Saved Charts</h2>
        <ChartList />
      </section> */}
    </div>
  );
}

export default App;
