import React, { useState } from "react";
import HighchartsReact from "highcharts-react-official";
import Highcharts from "highcharts";

const chartTypes = [
  "line", "bar", "column", "pie", "scatter", "area", "bubble", "heatmap",
  "treemap", "spline", "areaspline"
];

export default function ChartCreator({ data, onSave }) {
  const [chartType, setChartType] = useState("line");
  const [xKey, setXKey] = useState("");
  const [yKey, setYKey] = useState("");
  const [title, setTitle] = useState("My Chart");

  // Build series data based on selected keys and chart type
  const buildSeries = () => {
    if (!xKey || !yKey) return [];
    return [{
      name: yKey,
      data: data.map(row => [row[xKey], row[yKey]])
    }];
  };

  const options = {
    chart: { type: chartType },
    title: { text: title },
    xAxis: { title: { text: xKey } },
    yAxis: { title: { text: yKey } },
    series: buildSeries()
  };

  const handleSave = () => {
    const configJson = JSON.stringify(options);
    onSave(title, configJson);
  };

  const keys = data.length > 0 ? Object.keys(data[0]) : [];

  return (
    <div>
      <h3>Create Chart</h3>
      <input
        placeholder="Chart Title"
        value={title}
        onChange={e => setTitle(e.target.value)}
      />
      <select value={chartType} onChange={e => setChartType(e.target.value)}>
        {chartTypes.map(type => (
          <option key={type} value={type}>{type}</option>
        ))}
      </select>
      <select value={xKey} onChange={e => setXKey(e.target.value)}>
        <option value="">Select X-axis</option>
        {keys.map(k => <option key={k} value={k}>{k}</option>)}
      </select>
      <select value={yKey} onChange={e => setYKey(e.target.value)}>
        <option value="">Select Y-axis</option>
        {keys.map(k => <option key={k} value={k}>{k}</option>)}
      </select>

      <HighchartsReact highcharts={Highcharts} options={options} />

      <button onClick={handleSave}>Save Chart</button>
    </div>
  );
}
