// src/components/utils/highcharts-config.js
import Highcharts from 'highcharts';
import Exporting from 'highcharts/modules/exporting';
import Accessibility from 'highcharts/modules/accessibility';
import Drilldown from 'highcharts/modules/drilldown';
import OfflineExporting from 'highcharts/modules/offline-exporting';
import Annotations from 'highcharts/modules/annotations';



[Exporting, Accessibility, Drilldown, OfflineExporting, Annotations].forEach((mod) => {
  if (typeof mod === 'function') {
    mod(Highcharts);
  }
});

Highcharts.setOptions({
  chart: {
    backgroundColor: "#ffffff", // pure white
    style: {
      fontFamily: "'Inter', 'Segoe UI', 'Roboto', sans-serif",
      fontSize: "14px"
    },
    spacingTop: 16,
    spacingBottom: 16,
    spacingLeft: 16,
    spacingRight: 16,
  },
  title: {
    style: {
      fontSize: "20px",
      fontWeight: "600",
      color: "#222222",
    },
    align: "left",
    margin: 20,
  },
  subtitle: {
    style: {
      fontSize: "14px",
      color: "#666666",
    },
    align: "left",
  },
  tooltip: {
    backgroundColor: "#f9f9f9",
    borderColor: "#bbbbbb",
    borderRadius: 6,
    borderWidth: 1,
    shadow: true,
    style: {
      color: "#111111",
      fontSize: "13px",
      fontWeight: "normal"
    }
  },
  legend: {
    itemStyle: {
      color: "#333333",
      fontWeight: "500",
      fontSize: "13px",
    },
    itemHoverStyle: {
      color: "#000000"
    },
    itemHiddenStyle: {
      color: "#cccccc"
    }
  },
  xAxis: {
    labels: {
      style: {
        color: "#444",
        fontSize: "12px"
      }
    },
    title: {
      style: {
        color: "#333",
        fontSize: "13px"
      }
    },
    lineColor: "#ccc",
    tickColor: "#ccc"
  },
  yAxis: {
    labels: {
      style: {
        color: "#444",
        fontSize: "12px"
      }
    },
    title: {
      style: {
        color: "#333",
        fontSize: "13px"
      }
    },
    gridLineColor: "#eee"
  },
  plotOptions: {
    series: {
      dataLabels: {
        style: {
          fontSize: "12px"
        }
      }
    }
  },
  colors: [
    "#008FFB", "#00E396", "#FEB019", "#FF4560", "#775DD0",
    "#546E7A", "#26A69A", "#D10CE8", "#8D6E63", "#4CAF50"
  ]
});


export default Highcharts;
