import React, { useState } from "react";
import HighchartsReact from "highcharts-react-official";
import Highcharts from "../components/utils/highcharts-config";
import { Box, Button, ButtonGroup, Typography, Paper, Grid } from "@mui/material";
import { Link } from "react-router-dom";

const mainData = [
  { Year: "2010", TotalBudget: 11804.2, TotalResource: 10316.1, TotalCapital: 1488.1 },
  { Year: "2011", TotalBudget: 11520.4, TotalResource: 10329.1, TotalCapital: 1191.3 },
  { Year: "2012", TotalBudget: 11525.9, TotalResource: 10353.4, TotalCapital: 1172.5 },
  { Year: "2013", TotalBudget: 11548.7, TotalResource: 10431.9, TotalCapital: 1116.8 },
  { Year: "2014", TotalBudget: 11919.6, TotalResource: 10519.9, TotalCapital: 1399.7 },
  { Year: "2015", TotalBudget: 11293.8, TotalResource: 10176.1, TotalCapital: 1117.7 },
  { Year: "2016", TotalBudget: 11614, TotalResource: 10398, TotalCapital: 1216 },
  { Year: "2017", TotalBudget: 11845.1, TotalResource: 10612.8, TotalCapital: 1232.3 },
  { Year: "2018", TotalBudget: 12204.7, TotalResource: 10780.4, TotalCapital: 1424.3 },
  { Year: "2019", TotalBudget: 12741.6, TotalResource: 11351.5, TotalCapital: 1390.1 },
  { Year: "2020", TotalBudget: 13746.4, TotalResource: 12196.9, TotalCapital: 1549.5 },
  { Year: "2021", TotalBudget: 14782.5, TotalResource: 13001.5, TotalCapital: 1781 },
  { Year: "2022", TotalBudget: 16323.341, TotalResource: 14269.097, TotalCapital: 2054.244 },
  { Year: "2023", TotalBudget: 16451.684, TotalResource: 14211.967, TotalCapital: 2239.717 },
  { Year: "2024", TotalBudget: 17255.6, TotalResource: 15168.2, TotalCapital: 2087.4 },
  { Year: "2025", TotalBudget: 19085, TotalResource: 16639.4, TotalCapital: 2445.6 },
];

// Your drilldownData remains unchanged...

export default function DashboardPage() {
  const [view, setView] = useState("TotalBudget"); // options: TotalBudget, Resource, Capital
  const categories = mainData.map((d) => d.Year);

  // Build main series for line chart depending on the selected view
  const mainSeriesData = mainData.map((d) => ({
    y: d[view],
    drilldown: d.Year,
  }));

  // Colors per budget type for clarity
  const colors = {
    TotalBudget: "#1f77b4",
    Resource: "#ff7f0e",
    Capital: "#2ca02c",
  };

  // Build drilldown series dynamically based on the view selected
  const drilldownSeries = Object.entries(drilldownData).map(([year, data]) => ({
    id: year,
    name: `${view} Breakdown for ${year}`,
    type: "bar",
    data: data[view === "TotalBudget" ? "resource" : view.toLowerCase()].map((val, i) => [data.categories[i], val]),
    color: colors[view === "TotalBudget" ? "Resource" : view], // fallback to Resource colors for TotalBudget drilldown
  }));

  // Summaries to show at top
  const totalLatest = mainData[mainData.length - 1][view].toLocaleString(undefined, { maximumFractionDigits: 2 });
  const totalChange = ((mainData[mainData.length - 1][view] - mainData[0][view]) / mainData[0][view]) * 100;

  const options = {
    chart: {
      type: "line",
      height: 520,
      style: { fontFamily: "'Roboto', sans-serif" },
    },
    title: {
      text: `Budget Overview: ${view} (£M) - Click points for details`,
      style: { fontWeight: "bold" },
    },
    xAxis: {
      categories,
      title: { text: "Year" },
      crosshair: true,
    },
    yAxis: {
      min: 0,
      title: { text: "Budget (£M)" },
      labels: {
        formatter() {
          return `£${this.value}M`;
        },
      },
      gridLineDashStyle: "Dash",
    },
    tooltip: {
      shared: true,
      valuePrefix: "£",
      valueSuffix: "M",
      backgroundColor: "rgba(255,255,255,0.9)",
      borderColor: colors[view],
      borderRadius: 8,
      style: { color: "#000" },
    },
    plotOptions: {
      series: {
        cursor: "pointer",
        marker: { enabled: true, radius: 5 },
        lineWidth: 3,
        states: {
          hover: { lineWidth: 4 },
        },
      },
      bar: {
        grouping: true,
        borderRadius: 4,
      },
    },
    legend: {
      enabled: false,
    },
    series: [
      {
        name: view,
        data: mainSeriesData,
        color: colors[view],
        marker: { symbol: "circle" },
      },
    ],
    drilldown: {
      activeAxisLabelStyle: {
        textDecoration: "underline",
        fontWeight: "bold",
      },
      activeDataLabelStyle: {
        textDecoration: "underline",
        fontWeight: "bold",
      },
      series: drilldownSeries,
    },
  };

  return (
    <Box sx={{ maxWidth: 1000, margin: "auto", p: 3, fontFamily: "'Roboto', sans-serif" }}>
      <Typography variant="h4" gutterBottom align="center" fontWeight="700">
        Advanced Budget Dashboard
      </Typography>

      <Grid container spacing={3} justifyContent="center" sx={{ mb: 4 }}>
        {/* Summary Cards */}
        <Grid item xs={12} sm={4}>
          <Paper elevation={3} sx={{ p: 3, textAlign: "center" }}>
            <Typography variant="subtitle1" color="textSecondary" gutterBottom>
              Latest {view.replace(/([A-Z])/g, " $1").trim()}
            </Typography>
            <Typography variant="h5" fontWeight="bold" color={colors[view]}>
              £{totalLatest}M
            </Typography>
            <Typography
              variant="subtitle2"
              color={totalChange >= 0 ? "success.main" : "error.main"}
              sx={{ mt: 1 }}
            >
              {totalChange >= 0 ? "+" : ""}
              {totalChange.toFixed(2)}% (since 2010)
            </Typography>
          </Paper>
        </Grid>
      </Grid>

      <Box sx={{ textAlign: "center", mb: 4 }}>
        <ButtonGroup variant="outlined" aria-label="budget view toggle">
          <Button
            onClick={() => setView("TotalBudget")}
            variant={view === "TotalBudget" ? "contained" : "outlined"}
            color="primary"
          >
            Total Budget
          </Button>
          <Button
            onClick={() => setView("Resource")}
            variant={view === "Resource" ? "contained" : "outlined"}
            color="warning"
          >
            Resource Budget
          </Button>
          <Button
            onClick={() => setView("Capital")}
            variant={view === "Capital" ? "contained" : "outlined"}
            color="success"
          >
            Capital Budget
          </Button>
        </ButtonGroup>
      </Box>

      <HighchartsReact highcharts={Highcharts} options={options} />

      <Box textAlign="center" mt={5}>
        <Link
          to="/upload"
          style={{
            display: "inline-block",
            padding: "12px 28px",
            fontSize: 18,
            backgroundColor: "#007bff",
            color: "white",
            borderRadius: 8,
            textDecoration: "none",
            boxShadow: "0 3px 7px rgba(0,123,255,.6)",
            transition: "background-color 0.3s ease",
          }}
          onMouseEnter={e => e.currentTarget.style.backgroundColor = "#0056b3"}
          onMouseLeave={e => e.currentTarget.style.backgroundColor = "#007bff"}
        >
          Upload Your Data & Create Charts
        </Link>
      </Box>
    </Box>
  );
}
