import React, { useState } from "react";
import Highcharts from "highcharts";
import HighchartsReact from "highcharts-react-official";

const BudgetChart = () => {
  const mainData = [
    { year: 2010, budget: 11804.2, resource: 10316.1, capital: 1488.1 },
    { year: 2011, budget: 11520.4, resource: 10329.1, capital: 1191.3 },
    { year: 2012, budget: 11525.9, resource: 10353.4, capital: 1172.5 },
    { year: 2013, budget: 11548.7, resource: 10431.9, capital: 1116.8 },
    { year: 2014, budget: 11919.6, resource: 10519.9, capital: 1399.7 },
    { year: 2015, budget: 11293.8, resource: 10176.1, capital: 1117.7 },
    { year: 2016, budget: 11614, resource: 10398, capital: 1216 },
    { year: 2017, budget: 11845.1, resource: 10612.8, capital: 1232.3 },
    { year: 2018, budget: 12204.7, resource: 10780.4, capital: 1424.3 },
    { year: 2019, budget: 12741.6, resource: 11351.5, capital: 1390.1 },
    { year: 2020, budget: 13746.4, resource: 12196.9, capital: 1549.5 },
    { year: 2021, budget: 14782.5, resource: 13001.5, capital: 1781 },
    { year: 2022, budget: 16323.341, resource: 14269.097, capital: 2054.244 },
    { year: 2023, budget: 16451.684, resource: 14211.967, capital: 2239.717 },
    { year: 2024, budget: 17255.6, resource: 15168.2, capital: 2087.4 },
    { year: 2025, budget: 19085, resource: 16639.4, capital: 2445.6 },
  ];

  const drilldownData = {
    "2025": {
      categories: ["Health", "Education", "Transport"],
      resource: [8000, 5000, 2639.4],
      capital: [1200, 900, 345.6],
    },
    "2024": {
      categories: ["Health", "Education", "Transport"],
      resource: [7000, 5400, 2768.2],
      capital: [1100, 750, 237.4],
    },
    // Add additional years here...
  };

  const [selectedView, setSelectedView] = useState("TotalBudget");
  const [drilldownYear, setDrilldownYear] = useState(null);

  const categories = mainData.map((d) => d.year.toString());
  const totalBudget = mainData.map((d) => d.budget);
  const totalResource = mainData.map((d) => d.resource);
  const totalCapital = mainData.map((d) => d.capital);

  const getDrilldownCategories = (year) => {
    return drilldownData[year]?.categories || [];
  };

  const getDrilldownSeries = (year) => {
    if (!year || !drilldownData[year]) return [];

    return [
      {
        name: "Resource",
        data: drilldownData[year].resource,
        color: "#7cb5ec",
      },
      {
        name: "Capital",
        data: drilldownData[year].capital,
        color: "#434348",
      },
    ];
  };

  const chartOptions = {
    chart: {
      type: "column",
    },
    title: {
      text:
        selectedView === "TotalBudget"
          ? "Total Budgets Over the Years"
          : `Drilldown for ${drilldownYear}`,
    },
    xAxis: {
      categories:
        selectedView === "TotalBudget"
          ? categories
          : getDrilldownCategories(drilldownYear),
      crosshair: true,
    },
    yAxis: {
      min: 0,
      title: {
        text: "£ Million",
      },
    },
    tooltip: {
      shared: true,
    },
    plotOptions: {
      column: {
        point: {
          events: {
            click: function () {
              if (selectedView === "TotalBudget") {
                setDrilldownYear(this.category);
                setSelectedView("Drilldown");
              }
            },
          },
        },
      },
    },
    series:
      selectedView === "TotalBudget"
        ? [
            {
              name: "Total Budget",
              data: totalBudget,
              color: "#7cb5ec",
            },
          ]
        : getDrilldownSeries(drilldownYear),
  };

  const handleBack = () => {
    setSelectedView("TotalBudget");
    setDrilldownYear(null);
  };

  return (
    <div>
      {selectedView !== "TotalBudget" && (
        <button onClick={handleBack} style={{ marginBottom: "10px" }}>
          ← Back to Total Budget
        </button>
      )}
      <HighchartsReact highcharts={Highcharts} options={chartOptions} />
    </div>
  );
};

export default BudgetChart;
