import React, { useEffect, useRef, useState } from "react";
import maplibregl from "maplibre-gl";
import "maplibre-gl/dist/maplibre-gl.css";

// Import your JSON data
import schoolsData from "./data/schools.json";

export default function SchoolsMap() {
  const mapContainer = useRef(null);
  const map = useRef(null);
  const [filterType, setFilterType] = useState("All");

  // Use the imported JSON data here
  const filteredSchools =
    filterType === "All" ? schoolsData : schoolsData.filter((s) => s.type === filterType);

  useEffect(() => {
    if (map.current) return; // initialize map only once

    map.current = new maplibregl.Map({
      container: mapContainer.current,
      style: {
        version: 8,
        sources: {
          "esri-satellite": {
            type: "raster",
            tiles: [
              "https://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}",
            ],
            tileSize: 256,
            attribution:
              "Tiles © Esri — Source: Esri, i-cubed, USDA, USGS, and others",
          },
          "esri-labels": {
            type: "raster",
            tiles: [
              "https://services.arcgisonline.com/ArcGIS/rest/services/Reference/World_Boundaries_and_Places/MapServer/tile/{z}/{y}/{x}",
            ],
            tileSize: 256,
            attribution: "Labels © Esri",
          },
        },
        layers: [
          {
            id: "esri-satellite",
            type: "raster",
            source: "esri-satellite",
            minzoom: 0,
            maxzoom: 19,
          },
          {
            id: "esri-labels",
            type: "raster",
            source: "esri-labels",
            minzoom: 0,
            maxzoom: 19,
            paint: {
              "raster-opacity": 0.9,
            },
          },
        ],
      },
      center: [0.4749, 51.7356],
      zoom: 6,
    });
  }, []);

  useEffect(() => {
    if (!map.current) return;

    if (map.current.markers) {
      map.current.markers.forEach((m) => m.remove());
    }
    map.current.markers = [];

    filteredSchools.forEach((school) => {
      const popup = new maplibregl.Popup({ offset: 25 }).setHTML(
        `<strong>${school.name}</strong><br/>
         Type: ${school.type}<br/>
         Pupils KS4: ${school.pupilsKS4}<br/>
         Progress 8 Score: ${school.progress8Score}<br/>
         City: ${school.city}`
      );

      const marker = new maplibregl.Marker()
        .setLngLat([school.lng, school.lat])
        .setPopup(popup)
        .addTo(map.current);

      map.current.markers.push(marker);
    });
  }, [filteredSchools]);

  return (
    <div style={{ maxWidth: "900px", margin: "auto" }}>
      <h2>Schools Interactive Map (Satellite + Labels)</h2>

      <label>
        Filter by school type:{" "}
        <select
          value={filterType}
          onChange={(e) => setFilterType(e.target.value)}
          style={{ marginBottom: "1rem" }}
        >
          <option value="All">All</option>
          <option value="Academy">Academy</option>
          <option value="Comprehensive">Comprehensive</option>
        </select>
      </label>

      <div
        ref={mapContainer}
        style={{ height: "600px", width: "100%", borderRadius: "8px" }}
      />
    </div>
  );
}
