// src/components/ChartCreator.jsx
import React, { useState, useMemo } from "react";
import HighchartsReact from "highcharts-react-official";
import Highcharts from "./utils/highcharts-config";

const chartTypes = [
  "column",
  "bar",
  "line",
  "spline",
  "area",
  "areaspline",
  "pie",
  "scatter",
  "heatmap",
  "treemap",
];

export default function ChartCreator({ data, onSave }) {
  const keys = Object.keys(data[0] || {});
  const [chartType, setChartType] = useState("column");
  const [xKey, setXKey] = useState(() => keys[0] || "");
  const [yKey, setYKey] = useState(() => keys[1] || "");
  const [drilldownKeys, setDrilldownKeys] = useState([]);
  const [title, setTitle] = useState("My Chart");
  const [breadcrumb, setBreadcrumb] = useState("");

  const availableDrilldownKeys = keys.filter((k) => k !== xKey && k !== yKey);

  const handleDrilldownKeysChange = (e) => {
    const selectedOptions = Array.from(e.target.selectedOptions).map((o) => o.value);
    setDrilldownKeys(selectedOptions);
  };

  const { series, drilldown } = useMemo(() => {
    if (!xKey || !yKey) return { series: [], drilldown: undefined };

    const mainSeries = [];
    const drilldownSeries = [];

    data.forEach((row) => {
      const mainCategory = row[xKey];
      const yValue = Number(row[yKey]) || 0;

      mainSeries.push({
        name: mainCategory,
        y: yValue,
        drilldown: mainCategory,
      });

      const drilldownData = drilldownKeys.map((key) => [key, Number(row[key]) || 0]);

      drilldownSeries.push({
        id: mainCategory,
        name: `${mainCategory} Breakdown`,
        data: drilldownData,
      });
    });

    return {
      series: [
        {
          name: yKey,
          colorByPoint: true,
          data: mainSeries,
        },
      ],
      drilldown: {
        series: drilldownSeries,
      },
    };
  }, [data, xKey, yKey, drilldownKeys]);

  const options = {
    chart: {
      type: chartType,
      backgroundColor: "#f5f5f5",
      style: {
        fontFamily: "'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif",
      },
      events: {
        drilldown: function (e) {
          setBreadcrumb(`Drilled into: ${e.point.name}`);
        },
        drillup: function () {
          setBreadcrumb("");
        },
      },
    },
    title: {
      text: title,
      style: {
        fontSize: "20px",
      },
    },
    tooltip: {
      shared: true,
      useHTML: true,
      style: {
        padding: 8,
        fontSize: "14px",
      },
    },
    xAxis: {
      type: "category",
      title: { text: xKey },
    },
    yAxis: {
      title: { text: yKey },
    },
    legend: { enabled: false },
    plotOptions: {
      series: {
        borderWidth: 0,
        dataLabels: {
          enabled: true,
          format: "{point.name}: {point.y:.2f}",
        },
        cursor: "pointer",
      },
    },
    exporting: {
      enabled: true,
    },
    series,
    drilldown,
  };

  return (
    <div style={{ padding: "1rem", maxWidth: 800, margin: "auto" }}>
      <h2 style={{ marginBottom: 10 }}>ðŸ“Š Interactive Chart Builder</h2>

      <input
        placeholder="Chart Title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        style={{ marginBottom: 12, width: "100%", padding: 8, fontSize: 16 }}
      />

      <div style={{ display: "flex", gap: 12, flexWrap: "wrap", marginBottom: 12 }}>
        <select
          value={chartType}
          onChange={(e) => setChartType(e.target.value)}
          style={{ flex: 1, minWidth: 140, padding: 8 }}
        >
          {chartTypes.map((type) => (
            <option key={type} value={type}>
              {type.charAt(0).toUpperCase() + type.slice(1)}
            </option>
          ))}
        </select>

        <select
          value={xKey}
          onChange={(e) => {
            setXKey(e.target.value);
            setDrilldownKeys([]);
          }}
          style={{ flex: 1, minWidth: 140, padding: 8 }}
        >
          {keys.map((k) => (
            <option key={k} value={k}>
              {k}
            </option>
          ))}
        </select>

        <select
          value={yKey}
          onChange={(e) => {
            setYKey(e.target.value);
            setDrilldownKeys([]);
          }}
          style={{ flex: 1, minWidth: 180, padding: 8 }}
        >
          {keys.map((k) => (
            <option key={k} value={k}>
              {k}
            </option>
          ))}
        </select>

        <select
          multiple
          value={drilldownKeys}
          onChange={handleDrilldownKeysChange}
          style={{ flex: 2, minWidth: 240, padding: 8, height: 100 }}
          title="Select categories to show on drilldown (Ctrl/Cmd+click for multiple)"
        >
          {availableDrilldownKeys.map((k) => (
            <option key={k} value={k}>
              {k}
            </option>
          ))}
        </select>
      </div>

      {breadcrumb && (
        <div style={{ marginBottom: 8, fontStyle: "italic", color: "#333" }}>
          {breadcrumb}
        </div>
      )}

      <button
        onClick={() => Highcharts.charts[0].exportChart()}
        style={{
          marginBottom: 16,
          padding: "8px 16px",
          background: "#007bff",
          color: "#fff",
          border: "none",
          borderRadius: 4,
          cursor: "pointer",
        }}
      >
        ðŸ“¥ Export Chart
      </button>

      <HighchartsReact highcharts={Highcharts} options={options} />
    </div>
  );
}
