import React from "react";
import HighchartsReact from "highcharts-react-official";
import Highcharts from "../components/utils/highcharts-config";
import { useNavigate } from "react-router-dom";

const budgetData = [
  { Year: "2010", TotalBudget: 11804.2 },
  { Year: "2011", TotalBudget: 11520.4 },
  { Year: "2012", TotalBudget: 11525.9 },
  { Year: "2013", TotalBudget: 11548.7 },
  { Year: "2014", TotalBudget: 11919.6 },
  { Year: "2015", TotalBudget: 11293.8 },
  { Year: "2016", TotalBudget: 11614 },
  { Year: "2017", TotalBudget: 11845.1 },
  { Year: "2018", TotalBudget: 12204.7 },
  { Year: "2019", TotalBudget: 12741.6 },
  { Year: "2020", TotalBudget: 13746.4 },
  { Year: "2021", TotalBudget: 14782.5 },
  { Year: "2022", TotalBudget: 16323.341 },
  { Year: "2023", TotalBudget: 16451.684 },
  { Year: "2024", TotalBudget: 17255.6 },
  { Year: "2025", TotalBudget: 19085 },
];

export default function DashboardPage() {
  const navigate = useNavigate();

  const options = {
    chart: {
      type: "line",
      height: 500,
    },
    title: {
      text: "Total Budget Over Years (£M)",
    },
    xAxis: {
      categories: budgetData.map((d) => d.Year),
      title: { text: "Year" },
    },
    yAxis: {
      title: { text: "Total Budget (£M)" },
      labels: {
        formatter() {
          return `£${this.value}M`;
        },
      },
    },
    tooltip: {
      valueSuffix: "M",
      valuePrefix: "£",
      shared: true,
    },
    series: [
      {
        name: "Total Budget",
        data: budgetData.map((d) => d.TotalBudget),
        color: "#007bff",
      },
    ],
  };

  return (
    <div style={{ maxWidth: 900, margin: "auto", padding: "2rem" }}>
      <HighchartsReact highcharts={Highcharts} options={options} />

      <Link to="/upload">Go to Upload & Chart Builder</Link>
    </div>
  );
}
