import React, { useState } from "react";
import { uploadExcel, uploadCsv, getSheetNames } from "../services/api";
import ChartCreator from "./ChartCreator";

export default function FileUploader() {
  const [file, setFile] = useState(null);
  const [sheetNames, setSheetNames] = useState([]);
  const [selectedSheet, setSelectedSheet] = useState("");
  const [range, setRange] = useState("A1:B10");
  const [data, setData] = useState(null);
  const [fileType, setFileType] = useState("excel");

  const handleFileChange = async (e) => {
    const newFile = e.target.files[0];
    setFile(newFile);
    setData(null);
    setSheetNames([]);
    if (fileType === "excel") {
      try {
        const res = await getSheetNames(newFile); // New API call
        setSheetNames(res.data.sheets);
        setSelectedSheet(res.data.sheets[0]);
      } catch (err) {
        alert("Failed to get sheet names");
      }
    }
  };

  const handleUpload = async () => {
    if (!file) return;
    try {
      let res;
      if (fileType === "excel") {
        res = await uploadExcel(file, selectedSheet, range);
      } else {
        res = await uploadCsv(file);
      }
      setData(res.data);
    } catch (err) {
      alert("Upload failed");
    }
  };

  return (
    <div>
      <h3>Upload Data File</h3>
      <input type="file" onChange={handleFileChange} />
      <select value={fileType} onChange={(e) => setFileType(e.target.value)}>
        <option value="excel">Excel</option>
        <option value="csv">CSV</option>
      </select>

      {fileType === "excel" && sheetNames.length > 0 && (
        <>
          <label>Sheet:</label>
          <select value={selectedSheet} onChange={(e) => setSelectedSheet(e.target.value)}>
            {sheetNames.map((name, i) => (
              <option key={i} value={name}>{name}</option>
            ))}
          </select>
          <input
            type="text"
            value={range}
            onChange={(e) => setRange(e.target.value)}
            placeholder="Range (e.g., A1:B10)"
          />
        </>
      )}

      <button onClick={handleUpload}>Upload & Preview</button>

      {data && (
        <>
          <h4>Data Preview</h4>
          <table border="1" cellPadding={5}>
            <thead>
              <tr>{Object.keys(data[0]).map((key) => <th key={key}>{key}</th>)}</tr>
            </thead>
            <tbody>
              {data.map((row, i) => (
                <tr key={i}>{Object.values(row).map((val, j) => <td key={j}>{val}</td>)}</tr>
              ))}
            </tbody>
          </table>
          <ChartCreator data={data} />
        </>
      )}
    </div>
  );
}
