// src/components/ChartCreator.jsx
import React, { useState, useMemo } from "react";
import HighchartsReact from "highcharts-react-official";
import Highcharts from "./utils/highcharts-config";

const chartTypes = [
  "column", "bar", "pie", "line", "spline", "area", "scatter", "bubble"
];

const drilldownSupportedTypes = ["column", "bar", "pie"];

export default function ChartCreator({ data, onSave }) {
  const [chartType, setChartType] = useState("column");
  const [xKey, setXKey] = useState("");
  const [yKey, setYKey] = useState("");
  const [drilldownKey, setDrilldownKey] = useState("");
  const [title, setTitle] = useState("My Chart");

  const keys = useMemo(() => (data.length > 0 ? Object.keys(data[0]) : []), [data]);

  const isDrilldownEnabled = drilldownSupportedTypes.includes(chartType) && drilldownKey;

  const buildSeriesWithDrilldown = () => {
    if (!xKey || !yKey) return { series: [], drilldown: {} };

    const grouped = {};
    const drilldownSeries = [];

    data.forEach(row => {
      const main = row[xKey];
      const sub = row[drilldownKey];
      const yVal = parseFloat(row[yKey]);

      if (isNaN(yVal)) return;

      if (!grouped[main]) grouped[main] = { total: 0, drill: {} };
      grouped[main].total += yVal;

      if (isDrilldownEnabled && sub) {
        grouped[main].drill[sub] = (grouped[main].drill[sub] || 0) + yVal;
      }
    });

    const mainSeries = Object.entries(grouped).map(([name, { total, drill }]) => ({
      name,
      y: total,
      drilldown: isDrilldownEnabled && Object.keys(drill).length > 0 ? name : null
    }));

    if (isDrilldownEnabled) {
      Object.entries(grouped).forEach(([name, { drill }]) => {
        if (Object.keys(drill).length > 0) {
          drilldownSeries.push({
            id: name,
            name: `${name} Details`,
            data: Object.entries(drill)
          });
        }
      });
    }

    return {
      series: [{
        name: yKey,
        colorByPoint: true,
        data: mainSeries
      }],
      drilldown: {
        series: drilldownSeries
      }
    };
  };

  const { series, drilldown } = buildSeriesWithDrilldown();

  const chartOptions = {
    chart: { type: chartType },
    title: { text: title },
    xAxis: {
      type: "category",
      title: { text: xKey || null }
    },
    yAxis: {
      title: { text: yKey || null }
    },
    legend: { enabled: true },
    plotOptions: {
      series: {
        borderWidth: 0,
        dataLabels: { enabled: true }
      }
    },
    series,
    drilldown: isDrilldownEnabled ? drilldown : undefined
  };

  const handleSave = () => {
    const configJson = JSON.stringify(chartOptions, null, 2);
    onSave(title, configJson);
  };

  return (
    <div style={{ padding: "1rem" }}>
      <h3>Create Chart</h3>

      <input
        placeholder="Chart Title"
        value={title}
        onChange={e => setTitle(e.target.value)}
        style={{ marginBottom: 10, width: "100%", padding: 6 }}
      />

      <div style={{ display: "flex", gap: 10, flexWrap: "wrap", marginBottom: 10 }}>
        <select value={chartType} onChange={e => setChartType(e.target.value)}>
          {chartTypes.map(type => (
            <option key={type} value={type}>{type}</option>
          ))}
        </select>

        <select value={xKey} onChange={e => setXKey(e.target.value)}>
          <option value="">X-axis</option>
          {keys.map(k => <option key={k} value={k}>{k}</option>)}
        </select>

        <select value={yKey} onChange={e => setYKey(e.target.value)}>
          <option value="">Y-axis</option>
          {keys.map(k => <option key={k} value={k}>{k}</option>)}
        </select>

        <select
          value={drilldownKey}
          onChange={e => setDrilldownKey(e.target.value)}
          disabled={!drilldownSupportedTypes.includes(chartType)}
        >
          <option value="">Drilldown Key (optional)</option>
          {keys.map(k => <option key={k} value={k}>{k}</option>)}
        </select>
      </div>

      <HighchartsReact highcharts={Highcharts} options={chartOptions} />

      <button onClick={handleSave} style={{ marginTop: 10 }}>Save Chart</button>
    </div>
  );
}
