// src/components/ChartCreator.jsx
import React, { useState, useMemo } from "react";
import HighchartsReact from "highcharts-react-official";
import Highcharts from "./utils/highcharts-config"; // your custom Highcharts build with modules

const chartTypes = [
  "line",
  "bar",
  "column",
  "pie",
  "scatter",
  "area",
  "bubble",
  "spline",
  "areaspline",
];

export default function ChartCreator({ data, onSave }) {
  const [chartType, setChartType] = useState("column");
  const [xKey, setXKey] = useState("");
  const [yKey, setYKey] = useState("");
  const [drilldownKey, setDrilldownKey] = useState(""); // Optional drilldown key
  const [title, setTitle] = useState("My Chart");

  const keys = useMemo(() => (data.length > 0 ? Object.keys(data[0]) : []), [data]);

  // Build series and drilldown series for Highcharts drilldown feature
  const { series, drilldown } = useMemo(() => {
    if (!xKey || !yKey) return { series: [], drilldown: { series: [] } };

    // Group data by xKey and drilldownKey
    const grouped = {};
    const drilldownSeries = [];

    data.forEach((row) => {
      const main = row[xKey];
      const sub = drilldownKey ? row[drilldownKey] : null;
      const yVal = Number(row[yKey]) || 0;

      if (!grouped[main]) grouped[main] = { total: 0, drill: {} };

      grouped[main].total += yVal;

      if (sub) {
        grouped[main].drill[sub] = (grouped[main].drill[sub] || 0) + yVal;
      }
    });

    // Main series data points
    const mainSeries = Object.entries(grouped).map(([name, { total, drill }]) => ({
      name,
      y: total,
      drilldown: Object.keys(drill).length ? name : null,
    }));

    // Drilldown series data
    Object.entries(grouped).forEach(([name, { drill }]) => {
      if (Object.keys(drill).length) {
        drilldownSeries.push({
          id: name,
          name: `${name} details`,
          data: Object.entries(drill).map(([subName, value]) => [subName, value]),
        });
      }
    });

    return {
      series: [
        {
          name: yKey,
          colorByPoint: true,
          data: mainSeries,
        },
      ],
      drilldown: {
        series: drilldownSeries.length ? drilldownSeries : [],
        activeDataLabelStyle: {
          color: "#FFFFFF",
          textDecoration: "none",
          fontWeight: "normal",
        },
      },
    };
  }, [data, xKey, yKey, drilldownKey]);

  // Chart options
  const options = {
    chart: { type: chartType },
    title: { text: title },
    xAxis: {
      type: "category",
      title: { text: xKey || null },
    },
    yAxis: {
      title: { text: yKey || null },
    },
    legend: { enabled: true },
    plotOptions: {
      series: {
        borderWidth: 0,
        dataLabels: { enabled: true },
      },
    },
    series,
    drilldown,
  };

  // Save handler: export options as JSON
  const handleSave = () => {
    const configJson = JSON.stringify(options, null, 2);
    onSave(title, configJson);
  };

  return (
    <div style={{ padding: "1rem", maxWidth: 600 }}>
      <h3>Create Chart with Drilldown</h3>

      <input
        placeholder="Chart Title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        style={{ marginBottom: 12, width: "100%", padding: 6, fontSize: 16 }}
      />

      <div style={{ display: "flex", gap: 12, flexWrap: "wrap", marginBottom: 12 }}>
        <select
          value={chartType}
          onChange={(e) => setChartType(e.target.value)}
          style={{ flex: 1, minWidth: 120, padding: 6 }}
        >
          {chartTypes.map((type) => (
            <option key={type} value={type}>
              {type}
            </option>
          ))}
        </select>

        <select
          value={xKey}
          onChange={(e) => setXKey(e.target.value)}
          style={{ flex: 1, minWidth: 120, padding: 6 }}
        >
          <option value="">Select X-axis</option>
          {keys.map((k) => (
            <option key={k} value={k}>
              {k}
            </option>
          ))}
        </select>

        <select
          value={yKey}
          onChange={(e) => setYKey(e.target.value)}
          style={{ flex: 1, minWidth: 120, padding: 6 }}
        >
          <option value="">Select Y-axis</option>
          {keys.map((k) => (
            <option key={k} value={k}>
              {k}
            </option>
          ))}
        </select>

        <select
          value={drilldownKey}
          onChange={(e) => setDrilldownKey(e.target.value)}
          style={{ flex: 1, minWidth: 150, padding: 6 }}
        >
          <option value="">Drilldown Key (Optional)</option>
          {keys.map((k) => (
            <option key={k} value={k}>
              {k}
            </option>
          ))}
        </select>
      </div>

      <HighchartsReact highcharts={Highcharts} options={options} />

      <button
        onClick={handleSave}
        style={{
          marginTop: 12,
          padding: "8px 16px",
          fontSize: 16,
          cursor: "pointer",
        }}
        disabled={!xKey || !yKey}
        title={!xKey || !yKey ? "Select X and Y axis keys first" : "Save Chart Config"}
      >
        Save Chart
      </button>
    </div>
  );
}
