import React, { useState, useMemo } from "react";
import HighchartsReact from "highcharts-react-official";
import Highcharts from "./utils/highcharts-config";

const chartTypes = ["column", "bar", "line", "pie", "area"];

export default function ChartCreator({ data, onSave }) {
  const keys = Object.keys(data[0] || {});
  const [chartType, setChartType] = useState("column");
  const [xKey, setXKey] = useState(() => keys[0] || "");
  const [yKey, setYKey] = useState(() => keys[1] || "");
  const [drilldownKeys, setDrilldownKeys] = useState([]);
  const [title, setTitle] = useState("My Chart");

  const availableDrilldownKeys = keys.filter((k) => k !== xKey && k !== yKey);

  const handleDrilldownKeysChange = (e) => {
    const selected = Array.from(e.target.selectedOptions).map((o) => o.value);
    setDrilldownKeys(selected);
  };

  const { series, drilldown } = useMemo(() => {
    if (!xKey || !yKey) return { series: [], drilldown: undefined };

    const mainSeries = [];
    const drilldownSeries = [];

    data.forEach((row) => {
      const mainCategory = row[xKey];
      const yValue = Number(row[yKey]) || 0;

      mainSeries.push({
        name: mainCategory,
        y: yValue,
        drilldown: drilldownKeys.length > 0 ? mainCategory : null,
      });

      if (drilldownKeys.length > 0) {
        const drillData = drilldownKeys.map((k) => [k, Number(row[k]) || 0]);
        drilldownSeries.push({
          id: mainCategory,
          name: `${mainCategory} Breakdown`,
          data: drillData,
        });
      }
    });

    return {
      series: [
        {
          name: yKey,
          colorByPoint: true,
          data: mainSeries,
        },
      ],
      drilldown: {
        series: drilldownSeries,
      },
    };
  }, [data, xKey, yKey, drilldownKeys]);

  const options = {
    chart: {
      type: chartType,
      zoomType: "xy", // Enable zooming
      animation: true,
    },
    title: { text: title },
    xAxis: {
      type: "category",
      title: { text: xKey },
      labels: { rotation: -45 },
    },
    yAxis: {
      title: { text: yKey },
      allowDecimals: true,
    },
    legend: {
      enabled: true,
      itemStyle: { fontWeight: 'bold' },
    },
    tooltip: {
      headerFormat: "<b>{point.key}</b><br/>",
      pointFormat: "{series.name}: <b>{point.y}</b>",
      shared: true,
    },
    plotOptions: {
      series: {
        borderWidth: 0,
        dataLabels: {
          enabled: true,
          format: '{point.y:.2f}',
        },
        cursor: "pointer",
        point: {
          events: {
            click: function () {
              if (this.drilldown) {
                this.series.chart.addSeriesAsDrilldown(this, drilldown.series.find(s => s.id === this.name));
              }
            },
          },
        },
      },
    },
    drilldown,
    exporting: {
      enabled: true,
      buttons: {
        contextButton: {
          menuItems: ['downloadPNG', 'downloadCSV', 'downloadXLS'],
        },
      },
    },
    series,
    responsive: {
      rules: [
        {
          condition: {
            maxWidth: 600,
          },
          chartOptions: {
            xAxis: {
              labels: { rotation: -30 },
            },
          },
        },
      ],
    },
  };

  return (
    <div style={{ padding: "1rem", maxWidth: "100%", boxSizing: "border-box" }}>
      <h3>Create Interactive Chart</h3>

      <input
        placeholder="Chart Title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        style={{ marginBottom: 12, width: "100%", padding: 6, fontSize: 16 }}
      />

      <div style={{ display: "flex", gap: 12, flexWrap: "wrap", marginBottom: 12 }}>
        <select
          value={chartType}
          onChange={(e) => setChartType(e.target.value)}
          style={{ flex: 1, minWidth: 120, padding: 6 }}
        >
          {chartTypes.map((type) => (
            <option key={type} value={type}>
              {type}
            </option>
          ))}
        </select>

        <select
          value={xKey}
          onChange={(e) => {
            setXKey(e.target.value);
            setDrilldownKeys([]);
          }}
          style={{ flex: 1, minWidth: 120, padding: 6 }}
        >
          {keys.map((k) => (
            <option key={k} value={k}>
              {k}
            </option>
          ))}
        </select>

        <select
          value={yKey}
          onChange={(e) => {
            setYKey(e.target.value);
            setDrilldownKeys([]);
          }}
          style={{ flex: 1, minWidth: 180, padding: 6 }}
        >
          {keys.map((k) => (
            <option key={k} value={k}>
              {k}
            </option>
          ))}
        </select>

        <select
          multiple
          value={drilldownKeys}
          onChange={handleDrilldownKeysChange}
          style={{ flex: 1, minWidth: 250, padding: 6, height: 100 }}
          title="Select drilldown fields"
        >
          {availableDrilldownKeys.map((k) => (
            <option key={k} value={k}>
              {k}
            </option>
          ))}
        </select>
      </div>

      <HighchartsReact highcharts={Highcharts} options={options} />
    </div>
  );
}
