// frontend-react/src/components/ChartCreator.jsx
import React, { useState } from "react";
import Highcharts from "highcharts";
import HighchartsReact from "highcharts-react-official";
require("highcharts/modules/drilldown")(Highcharts); // ✅ THIS WORKS
import { saveChart } from "../services/api";

const chartTypes = [
  "column",
  "line",
  "pie",
  "bar",
  "area",
  "scatter"
];

export default function ChartCreator({ data }) {
  const [chartType, setChartType] = useState("column");
  const [chartName, setChartName] = useState("My Chart");
  const [options, setOptions] = useState(null);
  const [saveStatus, setSaveStatus] = useState(null);

  const buildOptions = () => {
    if (!data || data.length === 0) return;

    const categories = data.map((row) => Object.values(row)[0]);
    const values = data.map((row) => Number(Object.values(row)[1]) || 0);

    const chartOptions = {
      chart: { type: chartType },
      title: { text: chartName },
      xAxis: { categories },
      series: [{ name: "Data", data: values }],
      drilldown: { series: [] }, // Ready for drilldown config
    };

    setOptions(chartOptions);
  };

  const handleSave = async () => {
    if (!options) return;
    try {
      setSaveStatus("Saving...");
      const res = await saveChart(chartName, JSON.stringify(options));
      setSaveStatus("Saved with id: " + res.data.id);
    } catch {
      setSaveStatus("Save failed");
    }
  };

  return (
    <div>
      <h3>Create Chart</h3>
      <input
        type="text"
        value={chartName}
        onChange={(e) => setChartName(e.target.value)}
        placeholder="Chart name"
        style={{ marginRight: 10 }}
      />
      <select
        value={chartType}
        onChange={(e) => setChartType(e.target.value)}
        style={{ marginRight: 10 }}
      >
        {chartTypes.map((t) => (
          <option key={t} value={t}>
            {t}
          </option>
        ))}
      </select>
      <button onClick={buildOptions} disabled={!data}>
        Build Chart
      </button>
      <button onClick={handleSave} disabled={!options}>
        Save Chart
      </button>
      {saveStatus && <p>{saveStatus}</p>}
      {options && <HighchartsReact highcharts={Highcharts} options={options} />}
    </div>
  );
}
