import React, { useEffect, useRef, useState } from "react";
import maplibregl from "maplibre-gl";
import Papa from "papaparse";
import "maplibre-gl/dist/maplibre-gl.css";

export default function SchoolsMap() {
  const mapContainer = useRef(null);
  const map = useRef(null);
  const [schools, setSchools] = useState([]);
  const [filterType, setFilterType] = useState("All");

  const parseCoordinate = (coord) => {
    if (!coord) return null;
    let value = parseFloat(coord);
    if (coord.includes("S") || coord.includes("W")) value *= -1;
    return value;
  };

  useEffect(() => {
    Papa.parse("/data/DataSchools.csv", {
      download: true,
      header: true,
      complete: (results) => {
        const cleaned = results.data
          .map((row) => {
            const lat = parseCoordinate(row["Latitude"]);
            const lng = parseCoordinate(row["Longitude"]);
            if (!lat || !lng) return null;
            return {
              name: row["School Name"],
              type: row["Type"],
              pupilsKS4: row["Pupils KS4"],
              progress8Score: row["Progress 8 Score"],
              city: row["City"],
              lat,
              lng,
            };
          })
          .filter(Boolean);
        setSchools(cleaned);
      },
    });
  }, []);

  const filteredSchools =
    filterType === "All" ? schools : schools.filter((s) => s.type === filterType);

  useEffect(() => {
    if (map.current) return;

    map.current = new maplibregl.Map({
      container: mapContainer.current,
      style: {
        version: 8,
        sources: {
          "esri-satellite": {
            type: "raster",
            tiles: [
              "https://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}",
            ],
            tileSize: 256,
            attribution:
              "Tiles © Esri — Source: Esri, i-cubed, USDA, USGS, AEX, GeoEye, Getmapping, Aerogrid, IGN, IGP, and others",
          },
          "esri-labels": {
            type: "raster",
            tiles: [
              "https://services.arcgisonline.com/ArcGIS/rest/services/Reference/World_Boundaries_and_Places/MapServer/tile/{z}/{y}/{x}",
            ],
            tileSize: 256,
            attribution: "Labels © Esri",
          },
        },
        layers: [
          {
            id: "satellite",
            type: "raster",
            source: "esri-satellite",
            minzoom: 0,
            maxzoom: 19,
          },
          {
            id: "labels",
            type: "raster",
            source: "esri-labels",
            minzoom: 0,
            maxzoom: 19,
          },
        ],
      },
      center: [-1, 52.5],
      zoom: 6,
    });
  }, []);

  useEffect(() => {
    if (!map.current || !filteredSchools.length) return;

    if (map.current.markers) {
      map.current.markers.forEach((m) => m.remove());
    }
    map.current.markers = [];

    filteredSchools.forEach((school) => {
      const popup = new maplibregl.Popup({ offset: 25 }).setHTML(
        `<strong>${school.name}</strong><br/>
         Type: ${school.type}<br/>
         Pupils KS4: ${school.pupilsKS4}<br/>
         Progress 8 Score: ${school.progress8Score}<br/>
         City: ${school.city}`
      );

      const marker = new maplibregl.Marker({ color: "#d33" })
        .setLngLat([school.lng, school.lat])
        .setPopup(popup)
        .addTo(map.current);

      map.current.markers.push(marker);
    });
  }, [filteredSchools]);

  return (
    <div style={{ maxWidth: "1000px", margin: "auto" }}>
      <h2>UK Schools Map (From CSV)</h2>
      <label>
        Filter by type:{" "}
        <select
          value={filterType}
          onChange={(e) => setFilterType(e.target.value)}
          style={{ marginBottom: "1rem" }}
        >
          <option value="All">All</option>
          <option value="Academy">Academy</option>
          <option value="Independent">Independent</option>
        </select>
      </label>

      <div
        ref={mapContainer}
        style={{ height: "600px", width: "100%", borderRadius: "10px" }}
      />
    </div>
  );
}
